import React, { useState } from "react";
import axios from "axios";
// Assuming AddMedicineModal is also designed to be responsive
// import AddMedicineModal from "./AddMedicineModal"; 

// Placeholder for AddMedicineModal to make the code runnable without external files
const AddMedicineModal = ({ onClose, onAddMedicine }) => {
  const [medicineName, setMedicineName] = useState('');
  const [brandName, setBrandName] = useState('');
  const [mfgDate, setMfgDate] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [packedType, setPackedType] = useState('');
  const [stockQuantity, setStockQuantity] = useState('');
  const [purchasePrice, setPurchasePrice] = useState('');
  const [mrp, setMrp] = useState('');
  const [batchNo, setBatchNo] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddMedicine({
      medicine_name: medicineName,
      brand_name: brandName,
      mfg_date: mfgDate,
      expiry_date: expiryDate,
      packed_type: packedType,
      stock_quantity: parseInt(stockQuantity),
      purchase_price: parseFloat(purchasePrice),
      mrp: parseFloat(mrp),
      batch_no: batchNo,
    });
    onClose(); // Close modal after adding
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-md p-6 relative">
        <h2 className="text-xl font-bold mb-4 text-gray-800">Add New Medicine</h2>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Medicine Name</label>
            <input type="text" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={medicineName} onChange={(e) => setMedicineName(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Brand Name</label>
            <input type="text" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={brandName} onChange={(e) => setBrandName(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">MFG Date</label>
            <input type="date" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={mfgDate} onChange={(e) => setMfgDate(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Expiry Date</label>
            <input type="date" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={expiryDate} onChange={(e) => setExpiryDate(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Packed Type</label>
            <input type="text" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={packedType} onChange={(e) => setPackedType(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Quantity</label>
            <input type="number" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={stockQuantity} onChange={(e) => setStockQuantity(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Purchase Price</label>
            <input type="number" step="0.01" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={purchasePrice} onChange={(e) => setPurchasePrice(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">MRP</label>
            <input type="number" step="0.01" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={mrp} onChange={(e) => setMrp(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Batch No.</label>
            <input type="text" className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500" value={batchNo} onChange={(e) => setBatchNo(e.target.value)} required />
          </div>
          <div className="flex justify-end gap-2 mt-4">
            <button type="button" onClick={onClose} className="px-4 py-2 bg-gray-200 text-gray-800 rounded-md hover:bg-gray-300">Cancel</button>
            <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700">Add Medicine</button>
          </div>
        </form>
      </div>
    </div>
  );
};


const BASE_URL = "http://localhost:4000";
// In a real application, consider managing tokens more securely (e.g., React Context, Redux)
// and fetching them dynamically rather than directly from localStorage at the top level.
const token = localStorage.getItem('token') || "";

function StockEntry() {
  const [wholesaler, setWholesaler] = useState("");
  const [invoiceNumber, setInvoiceNumber] = useState("");
  const [date, setDate] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  /**
   * Handles adding a new medicine object to the medicines array.
   * @param {object} medicine - The medicine object to add.
   */
  const handleAddMedicine = (medicine) => {
    setMedicines(prev => [...prev, medicine]);
  };

  /**
   * Handles deleting a medicine from the medicines array by its index.
   * @param {number} idx - The index of the medicine to delete.
   */
  const handleDeleteMedicine = (idx) => {
    setMedicines(prev => prev.filter((_, i) => i !== idx));
  };

  /**
   * Handles the form submission, sending stock entry data to the backend.
   * @param {Event} e - The form submission event.
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setMessage(null);

    // Basic client-side validation
    if (!wholesaler || !invoiceNumber || !date) {
      setError("Please fill all required fields.");
      return;
    }
    if (medicines.length === 0) {
      setError("Please add at least one medicine.");
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post(`${BASE_URL}/admin/medicine_stock`, {
        wholesaler,
        invoiceNumber,
        date,
        medicine: medicines // Ensure backend expects 'medicine' as an array
      }, {
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}` // Ensure token is valid and sent
        }
      });

      const data = res.data;
      if (res.status === 200) {
        setMessage(data.message || "Stock entry added successfully!");
        // Clear form fields on successful submission
        setWholesaler("");
        setInvoiceNumber("");
        setDate("");
        setMedicines([]);
      } else {
        // Handle non-200 responses from the API
        setError(data.message || "Failed to add stock entry. Please try again.");
      }
    } catch (err) {
      // Handle network errors or errors from the server response
      if (err.response?.data?.message) {
        setError(err.response.data.message);
      } else {
        setError("Network error or failed to connect to the server. Please check your connection.");
      }
    } finally {
      setLoading(false);
      // Automatically clear messages after a few seconds
      setTimeout(() => {
        setMessage(null);
        setError(null);
      }, 3000);
    }
  };

  return (
    <div className="w-full min-h-screen flex flex-col font-sans bg-gray-100">
      {/* Header */}
      <header className="flex items-center px-4 py-4 bg-theme-100 shadow-md sticky top-0 z-10">
        {/* Back button with a larger touch target for mobile */}
        <span 
          className="text-xl sm:text-2xl mr-3 cursor-pointer text-theme-800 p-2 rounded-full hover:bg-theme-200 transition-colors" 
          onClick={() => window.history.back()}
          aria-label="Go back"
        >
          &larr; {/* Left arrow character */}
        </span>
        <h1 className="text-lg sm:text-xl font-bold text-theme-800">Add New Stock Entry</h1>
      </header>

      {/* Form Container */}
      <form
        onSubmit={handleSubmit}
        className="w-full max-w-5xl mx-auto bg-white rounded-lg shadow-lg px-4 sm:px-6 py-6 mt-6 sm:mt-8 mb-8"
      >
        {/* Input Fields Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mb-6">
          <div>
            <label htmlFor="wholesaler" className="block font-medium mb-1 text-gray-700">Wholesaler <span className="text-theme-500">*</span></label>
            <input 
              id="wholesaler"
              className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-theme-500 focus:border-theme-500 focus:outline-none transition-colors" 
              value={wholesaler} 
              onChange={(e) => setWholesaler(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label htmlFor="invoiceNumber" className="block font-medium mb-1 text-gray-700">Invoice Number <span className="text-theme-500">*</span></label>
            <input 
              id="invoiceNumber"
              className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-theme-500 focus:border-theme-500 focus:outline-none transition-colors" 
              value={invoiceNumber} 
              onChange={(e) => setInvoiceNumber(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label htmlFor="date" className="block font-medium mb-1 text-gray-700">Date <span className="text-theme-500">*</span></label>
            <input 
              id="date"
              type="date" 
              className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-theme-500 focus:border-theme-500 focus:outline-none transition-colors" 
              value={date} 
              onChange={(e) => setDate(e.target.value)} 
              required 
            />
          </div>
        </div>

        {/* Medicine Table Section */}
        <div className="mb-4">
          <div className="flex justify-between items-center mb-4 flex-wrap gap-3">
            <h2 className="font-semibold text-lg text-theme-800">Medicines in this Invoice</h2>
            <button
              type="button"
              className="flex items-center bg-theme-600 text-white px-5 py-2 rounded-md hover:bg-theme-700 transition-colors shadow-sm text-base font-medium"
              onClick={() => setShowModal(true)}
            >
              <span className="text-xl mr-2 leading-none">+</span> Add Medicine
            </button>
          </div>
          {/* Responsive Table Container */}
          <div className="overflow-x-auto rounded-lg border border-theme-200 shadow-sm">
            <table className="min-w-full text-sm divide-y divide-theme-200">
              <thead className="bg-theme-100 text-theme-800">
                <tr>
                  {/* Added min-w classes for better column sizing on smaller screens if needed */}
                  {["Medicine Name", "Brand", "MFG Date", "Expiry Date", "Packed Type", "Qty", "Purchase", "MRP", "Batch", "Action"]
                    .map((col, i) => (
                      <th key={i} className="px-3 py-3 text-left font-semibold whitespace-nowrap">{col}</th>
                    ))}
                </tr>
              </thead>
              <tbody className="divide-y divide-theme-100">
                {medicines.length === 0 ? (
                  <tr>
                    <td colSpan={10} className="text-center py-6 text-gray-400 italic">No medicines added yet. Click "Add Medicine" to start.</td>
                  </tr>
                ) : medicines.map((med, idx) => (
                  <tr key={idx} className={idx % 2 === 0 ? "bg-white" : "bg-theme-50"}>
                    <td className="px-3 py-2 whitespace-nowrap">{med.medicine_name}</td>
                    <td className="px-3 py-2 whitespace-nowrap">{med.brand_name}</td>
                    <td className="px-3 py-2 whitespace-nowrap">{med.mfg_date}</td>
                    <td className="px-3 py-2 whitespace-nowrap">{med.expiry_date}</td>
                    <td className="px-3 py-2 whitespace-nowrap">{med.packed_type}</td>
                    <td className="px-3 py-2 whitespace-nowrap">{med.stock_quantity}</td>
                    <td className="px-3 py-2 whitespace-nowrap">{med.purchase_price}</td>
                    <td className="px-3 py-2 whitespace-nowrap">{med.mrp}</td>
                    <td className="px-3 py-2 whitespace-nowrap">{med.batch_no}</td>
                    <td className="px-3 py-2">
                      <button
                        type="button"
                        className="text-theme-500 hover:text-red-600 p-1 rounded-full hover:bg-red-100 transition-colors"
                        onClick={() => handleDeleteMedicine(idx)}
                        aria-label={`Delete ${med.medicine_name}`}
                      >
                        üóëÔ∏è
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Messages */}
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-md relative mb-4" role="alert">
            <strong className="font-bold">Error!</strong>
            <span className="block sm:inline ml-2">{error}</span>
          </div>
        )}
        {message && (
          <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded-md relative mb-4" role="alert">
            <strong className="font-bold">Success!</strong>
            <span className="block sm:inline ml-2">{message}</span>
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex flex-col sm:flex-row justify-end items-center gap-3 mt-6">
          <button
            type="button"
            onClick={() => {
              // Reset all form fields and messages
              setWholesaler("");
              setInvoiceNumber("");
              setDate("");
              setMedicines([]);
              setError(null);
              setMessage(null);
            }}
            className="w-full sm:w-auto px-6 py-2 rounded-md bg-theme-200 text-theme-800 hover:bg-theme-300 transition-colors font-medium shadow-sm"
            disabled={loading}
          >
            Cancel
          </button>
          <button
            type="submit"
            className="w-full sm:w-auto px-6 py-2 rounded-md bg-theme-600 text-white hover:bg-theme-700 transition-colors font-medium shadow-md disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={loading}
          >
            {loading ? "Submitting..." : "Submit Stock Entry"}
          </button>
        </div>
      </form>

      {/* Add Medicine Modal */}
      {showModal && (
        <AddMedicineModal
          onClose={() => setShowModal(false)}
          onAddMedicine={handleAddMedicine}
        />
      )}
    </div>
  );
}

export default StockEntry;
