import React, { useState } from 'react';
import { Home as HomeIcon, Plus, Package, BarChart3, Menu, X, ChevronRight } from 'lucide-react';

const menuItems = [
  { name: 'Dashboard', icon: HomeIcon },
  { name: 'Add Stock', icon: Plus },
  { name: 'Stock', icon: Package },
  { name: 'Report', icon: BarChart3 }
];

const pageContent = {
  Dashboard: (
    <div>
      <h1 className="text-2xl font-bold mb-2">Dashboard</h1>
      <p className="text-gray-600">Welcome to your dashboard.</p>
    </div>
  ),
  'Add Stock': (
    <div>
      <h1 className="text-2xl font-bold mb-2">Add Stock</h1>
      <p className="text-gray-600">Add new medicines to your inventory.</p>
    </div>
  ),
  Stock: (
    <div>
      <h1 className="text-2xl font-bold mb-2">Stock</h1>
      <p className="text-gray-600">View and manage your current stock.</p>
    </div>
  ),
  Report: (
    <div>
      <h1 className="text-2xl font-bold mb-2">Report</h1>
      <p className="text-gray-600">View reports and analytics.</p>
    </div>
  )
};

const Home = () => {
  const [active, setActive] = useState('Dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <div className="min-h-screen flex bg-gray-50">
      {/* Sidebar */}
      <div className={`
        fixed z-30 inset-y-0 left-0 w-64 bg-sky-100 border-r border-sky-200 transform
        ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        transition-transform duration-200 ease-in-out
        lg:translate-x-0 lg:static lg:inset-0
      `}>
        <div className="flex items-center justify-between p-4 border-b border-sky-200">
          <span className="text-xl font-bold text-sky-700">pharmaDesk</span>
          <button className="lg:hidden" onClick={() => setSidebarOpen(false)}>
            <X className="w-6 h-6 text-gray-500" />
          </button>
        </div>
        <nav className="mt-4">
          <ul>
            {menuItems.map(item => (
              <li key={item.name}>
                <button
                  className={`flex items-center w-full px-6 py-3 text-left space-x-3 transition
                    ${active === item.name
                      ? 'bg-sky-500 text-white font-semibold'
                      : 'text-gray-700 hover:bg-sky-200 hover:text-sky-900'
                    }`}
                  onClick={() => {
                    setActive(item.name);
                    setSidebarOpen(false);
                  }}
                >
                  <item.icon className="w-5 h-5" />
                  <span>{item.name}</span>
                </button>
              </li>
            ))}
          </ul>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col lg:ml-64">
        {/* Top Bar with Breadcrumb */}
        <header className="sticky top-0 z-20 bg-gray-100 border-b border-gray-200 flex items-center px-4 py-3">
          <button
            className="lg:hidden mr-3 p-2 rounded-md text-gray-500 hover:text-gray-700 hover:bg-gray-200"
            onClick={() => setSidebarOpen(true)}
          >
            <Menu className="w-6 h-6" />
          </button>
          <nav className="flex items-center space-x-2 text-sm text-gray-600">
            <span className="capitalize">Home</span>
            <ChevronRight className="w-4 h-4 text-gray-400" />
            <span className="font-semibold text-gray-900">{active}</span>
          </nav>
        </header>
        {/* Main Page Content */}
        <main className="flex-1 p-6">
          <div className="max-w-3xl">{pageContent[active]}</div>
        </main>
      </div>
    </div>
  );
};

export default Home;
