import React, { useEffect, useState } from "react";
import axios from "axios";
import BoxLoader from "./looader/BoxLoader";

const BASE_URL = "http://localhost:4000"; // Change to your backend URL

function WholesalerPage() {
  const [wholesalers, setWholesalers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortOption, setSortOption] = useState("recent");
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    contact: "",
    address: "",
    gst_no: "",
  });

  const token = localStorage.getItem("token");

  const headers = {
    Authorization: `Bearer ${token}`,
  };

  const fetchWholesalers = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${BASE_URL}/admin/wholesalers`, {
        headers,
      });
      setWholesalers(response.data || []);
    } catch (err) {
      console.error("Error fetching wholesalers:", err);
    } finally {
      setLoading(false);
    }
  };

  const addWholesaler = async () => {
    try {
      setLoading(true);
      const response = await axios.post(
        `${BASE_URL}/admin/wholesalers`,
        formData,
        { headers }
      );
      setShowModal(false);
      setFormData({ name: "", contact: "", address: "", gst_no: "" });
      fetchWholesalers();
    } catch (err) {
      console.error("Error adding wholesaler:", err);
    } finally {
      setLoading(false);
    }
  };

  const deleteWholesaler = async (id) => {
    try {
      setLoading(true);
      await axios.delete(`${BASE_URL}/admin/wholesalers/${id}`, { headers });
      fetchWholesalers();
    } catch (err) {
      console.error("Error deleting wholesaler:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWholesalers();
  }, []);

  const filtered = wholesalers.filter((wh) =>
    wh.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const sorted = [...filtered].sort((a, b) => {
    if (sortOption === "recent") return new Date(b.created_at) - new Date(a.created_at);
    if (sortOption === "old") return new Date(a.created_at) - new Date(b.created_at);
    return 0;
  });

  return (
    <div className={`p-6 relative ${showModal ? "blur-sm" : ""}`}>
      <div className="flex justify-between mb-4">
        <div className="flex gap-2">
          <input
            type="text"
            placeholder="Search by name"
            className="px-2 py-1 border rounded"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <select
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
            className="px-2 py-1 border rounded"
          >
            <option value="recent">Recent</option>
            <option value="old">Old</option>
          </select>
        </div>
        <button
          className="bg-theme-600 hover:bg-theme-700 text-white px-4 py-1 rounded"
          onClick={() => setShowModal(true)}
        >
          + Add Wholesaler
        </button>
      </div>

      {loading ? (
        <BoxLoader />
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border border-gray-200 rounded">
            <thead>
              <tr className="bg-gray-100">
                <th className="py-2 px-4 text-left">Name</th>
                <th className="py-2 px-4 text-left">Contact</th>
                <th className="py-2 px-4 text-left">Address</th>
                <th className="py-2 px-4 text-left">GST No</th>
                <th className="py-2 px-4 text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {sorted.map((wh) => (
                <tr key={wh.wholesaler_id} className="border-t">
                  <td className="py-2 px-4">{wh.name}</td>
                  <td className="py-2 px-4">{wh.contact}</td>
                  <td className="py-2 px-4">{wh.address}</td>
                  <td className="py-2 px-4">{wh.gst_no}</td>
                  <td className="py-2 px-4 text-center">
                    <button
                      onClick={() => deleteWholesaler(wh.wholesaler_id)}
                      className="text-red-500 hover:text-red-700"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
              {sorted.length === 0 && (
                <tr>
                  <td colSpan="5" className="text-center py-4 text-gray-400">
                    No wholesalers found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-xl w-full max-w-md">
            <h2 className="text-xl font-semibold mb-4">Add Wholesaler</h2>
            <input
              className="w-full mb-2 p-2 border rounded"
              placeholder="Name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            />
            <input
              className="w-full mb-2 p-2 border rounded"
              placeholder="Contact"
              value={formData.contact}
              onChange={(e) => setFormData({ ...formData, contact: e.target.value })}
            />
            <input
              className="w-full mb-2 p-2 border rounded"
              placeholder="Address"
              value={formData.address}
              onChange={(e) => setFormData({ ...formData, address: e.target.value })}
            />
            <input
              className="w-full mb-4 p-2 border rounded"
              placeholder="GST No"
              value={formData.gst_no}
              onChange={(e) => setFormData({ ...formData, gst_no: e.target.value })}
            />
            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowModal(false)}
                className="px-4 py-1 rounded bg-gray-300 hover:bg-gray-400"
              >
                Cancel
              </button>
              <button
                onClick={addWholesaler}
                className="px-4 py-1 rounded bg-theme-600 text-white hover:bg-theme-700"
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default WholesalerPage;
