import React, { useState, useRef } from "react"; // Import useRef
import axios from "axios";
import AddMedicineModal from "./AddMedicineModal";
import RandomColorLoader from "../../looader/RandomColorLoader";

// Add UploadCloud and AlertTriangle icons
import { PlusCircle, Trash2, Loader2, PackagePlus, UploadCloud, AlertTriangle } from "lucide-react";

const BASE_URL = "http://localhost:4000";
const token = localStorage.getItem("lalitkumar_choudhary") || "";

function StockEntryResponsive() {
  const [wholesaler, setWholesaler] = useState("");
  const [invoiceNumber, setInvoiceNumber] = useState("");
  const [date, setDate] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [showAddMedicineModal, setShowAddMedicineModal] = useState(false);
  const [loading, setLoading] = useState(false); // For final form submission
  const [isProcessing, setIsProcessing] = useState(false); // For AI processing
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const fileInputRef = useRef(null); // Ref for the hidden file input

  // --- HANDLERS ---
  const handleAddMedicine = (medicine) => setMedicines((prev) => [...prev, medicine]);
  const handleDeleteMedicine = (idx) => setMedicines((prev) => prev.filter((_, i) => i !== idx));
  const clearForm = () => { /* ... (no changes) ... */ };
  
  // --- NEW: Handler for processing the uploaded invoice ---
  const handleFileProcess = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setIsProcessing(true);
    setError(null);
    setMessage(null);

    const formData = new FormData();
    formData.append("invoice", file); // Key 'invoice' must match backend (upload.single('invoice'))

    try {
      const res = await axios.post(`${BASE_URL}/admin/api/process-invoice`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
      });

      const data = res.data;
      // Auto-fill the form with data from Gemini
      setWholesaler(data.wholesaler || "");
      setInvoiceNumber(data.invoiceNumber || "");
      setDate(data.date || "");
      setMedicines(data.medicines || []);

      setMessage("Invoice processed! Please review the auto-filled details before submitting.");
    } catch (err) {
      setError(err.response?.data?.message || "Failed to process the invoice file. Please try a clearer image/PDF.");
    } finally {
      setIsProcessing(false);
      // Clear the file input value so the same file can be selected again
      if(fileInputRef.current) {
        fileInputRef.current.value = "";
      }
    }
  };


  const handleSubmit = async (e) => { /* ... (no changes) ... */ };

  return (
    <>
      <div className="min-h-screen bg-slate-100 p-4 sm:p-6 lg:p-8">
        <div className="mx-auto max-w-7xl">
          <div className="grid grid-cols-1 gap-8 lg:grid-cols-5">
            {/* --- LEFT COLUMN: FORM DETAILS --- */}
            <div className="lg:col-span-2">
              <div className="sticky p-6 bg-white rounded-lg shadow-md top-8">
                
                {/* --- NEW: AUTO-FILL SECTION --- */}
                <div className="p-4 mb-6 border-2 border-dashed rounded-lg border-indigo-200 bg-indigo-50">
                  <h3 className="text-lg font-bold text-slate-800">Auto-fill with AI</h3>
                  <p className="mb-3 text-sm text-slate-600">Upload an invoice (PDF/JPG/PNG) to let AI fill the form for you.</p>
                  <input
                    type="file"
                    ref={fileInputRef}
                    className="hidden"
                    accept=".pdf,.jpg,.jpeg,.png"
                    onChange={handleFileProcess}
                    disabled={isProcessing}
                  />
                  <button
                    type="button"
                    onClick={() => fileInputRef.current.click()}
                    disabled={isProcessing}
                    className="flex items-center justify-center w-full px-4 py-2 font-semibold text-white bg-indigo-600 rounded-md shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 disabled:bg-indigo-300"
                  >
                    {isProcessing ? (
                      <>
                        <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                        Analyzing Invoice...
                      </>
                    ) : (
                      <>
                        <UploadCloud className="w-5 h-5 mr-2" />
                        Upload Invoice
                      </>
                    )}
                  </button>
                </div>
                
                <h2 className="mb-6 text-2xl font-bold text-slate-800">
                  Stock Entry Details
                </h2>
                <form onSubmit={handleSubmit} className="space-y-5">
                  {/* ... (Your form inputs for Wholesaler, Invoice, Date remain the same) ... */}
                   <div>
                    <label htmlFor="wholesaler" className="block mb-1 text-sm font-medium text-slate-600">
                      Wholesaler <span className="text-red-500">*</span>
                    </label>
                    <input id="wholesaler" type="text" className="w-full px-3 py-2 border rounded-md border-slate-300 focus:outline-none focus:ring-2 focus:ring-indigo-500" value={wholesaler} onChange={(e) => setWholesaler(e.target.value)} required />
                  </div>
                  <div>
                    <label htmlFor="invoiceNumber" className="block mb-1 text-sm font-medium text-slate-600">
                      Invoice No <span className="text-red-500">*</span>
                    </label>
                    <input id="invoiceNumber" type="text" className="w-full px-3 py-2 border rounded-md border-slate-300 focus:outline-none focus:ring-2 focus:ring-indigo-500" value={invoiceNumber} onChange={(e) => setInvoiceNumber(e.target.value)} required />
                  </div>
                  <div>
                    <label htmlFor="date" className="block mb-1 text-sm font-medium text-slate-600">
                      Date <span className="text-red-500">*</span>
                    </label>
                    <input id="date" type="date" className="w-full px-3 py-2 border rounded-md border-slate-300 focus:outline-none focus:ring-2 focus:ring-indigo-500" value={date} onChange={(e) => setDate(e.target.value)} required />
                  </div>


                  {/* --- MESSAGES --- */}
                   <div className="pt-4 space-y-3">
                    {error && (
                      <div className="p-3 text-sm text-red-800 bg-red-100 rounded-lg" role="alert">
                        {error}
                      </div>
                    )}
                    {message && (
                      <div className="p-3 text-sm text-green-800 bg-green-100 rounded-lg" role="alert">
                        {message}
                      </div>
                    )}
                  </div>
                  
                  {/* --- ACTION BUTTONS --- */}
                  <div className="flex flex-col gap-3 pt-6 sm:flex-row">
                    <button
                      type="submit"
                      className="flex items-center justify-center w-full px-6 py-2.5 font-semibold text-white bg-indigo-600 rounded-md shadow-sm sm:w-auto hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 disabled:bg-indigo-300"
                      disabled={loading || isProcessing} // Also disable while AI is working
                    >
                      {loading ? (
                        <> <Loader2 className="w-5 h-5 mr-2 animate-spin" /> Submitting... </>
                      ) : ( "Submit Entry" )}
                    </button>
                    <button
                      type="button"
                      onClick={clearForm}
                      className="w-full px-6 py-2.5 font-semibold text-slate-700 bg-slate-200 rounded-md sm:w-auto hover:bg-slate-300 focus:outline-none focus:ring-2 focus:ring-slate-400 focus:ring-offset-2 disabled:opacity-50"
                      disabled={loading || isProcessing}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            </div>

            {/* --- RIGHT COLUMN: MEDICINE LIST (no changes here) --- */}
            {/* ... */}

          </div>
        </div>
      </div>
      {showAddMedicineModal && ( <AddMedicineModal onClose={() => setShowAddMedicineModal(false)} onAddMedicine={handleAddMedicine} /> )}
    </>
  );
}

export default StockEntryResponsive;
