import React, { useState } from "react";
import axios from "axios";
import AddMedicineModal from "./AddMedicineModal"; // Ensure you have this
import { Shell } from "lucide-react";

const BASE_URL = "http://localhost:4000";
const token = localStorage.getItem("token") || "";

function StockEntry() {
  const [wholesaler, setWholesaler] = useState("");
  const [invoiceNumber, setInvoiceNumber] = useState("");
  const [date, setDate] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [showAddMedicineModal, setShowAddMedicineModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleAddMedicine = (medicine) => {
    setMedicines((prev) => [...prev, medicine]);
  };

  const handleDeleteMedicine = (idx) => {
    setMedicines((prev) => prev.filter((_, i) => i !== idx));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setMessage(null);

    if (!wholesaler || !invoiceNumber || !date) {
      setError("Please fill all required fields.");
      return;
    }
    if (medicines.length === 0) {
      setError("Please add at least one medicine.");
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post(
        `${BASE_URL}/admin/medicine_stock`,
        {
          wholesaler,
          invoiceNumber,
          date,
          medicine: medicines,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (res.status === 200) {
        setMessage(res.data.message || "Stock entry added successfully!");
        setWholesaler("");
        setInvoiceNumber("");
        setDate("");
        setMedicines([]);
      } else {
        setError(res.data.message || "Failed to add stock entry.");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Server error. Please try again.");
    } finally {
      setLoading(false);
      setTimeout(() => {
        setMessage(null);
        setError(null);
      }, 3000);
    }
  };

  return (
    <div className="flex flex-col w-full px-4 py-6 mx-auto font-sans bg-gray-100 max-w-7xl">
      <form
        onSubmit={handleSubmit}
        className="w-full px-4 py-6 bg-white rounded-lg shadow-lg"
      >
        <h2 className="mb-4 text-xl font-bold text-theme-800">Stock Entry</h2>

        {/* Input fields */}
        <div className="grid grid-cols-1 gap-4 mb-6 sm:grid-cols-2 md:grid-cols-3">
          <div>
            <label className="block mb-1 font-medium text-gray-700">
              Wholesaler <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
              value={wholesaler}
              onChange={(e) => setWholesaler(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="block mb-1 font-medium text-gray-700">
              Invoice No <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
              value={invoiceNumber}
              onChange={(e) => setInvoiceNumber(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="block mb-1 font-medium text-gray-700">
              Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>
        </div>

        {/* Medicine Table Section */}
        <div className="mb-6">
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-lg font-semibold text-theme-800">
              Medicine List
            </h3>
            <button
              type="button"
              onClick={() => setShowAddMedicineModal(true)}
              className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700"
            >
              + Add Medicine
            </button>
          </div>

          <div className="overflow-x-auto border rounded-lg shadow-sm border-theme-200">
            <div className="min-w-[950px] md:min-w-full">
              <table className="w-full text-sm divide-y divide-theme-200">
                <thead className="text-theme-800 bg-theme-100">
                  <tr>
                    {[
                      "Medicine Name",
                      "Brand",
                      "MFG Date",
                      "Expiry Date",
                      "Packed Type",
                      "Qty",
                      "Purchase",
                      "MRP",
                      "Batch",
                      "Action",
                    ].map((col, i) => (
                      <th
                        key={i}
                        className="px-3 py-3 text-left font-semibold whitespace-nowrap"
                      >
                        {col}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody className="divide-y divide-theme-100">
                  {medicines.length === 0 ? (
                    <tr>
                      <td
                        colSpan={10}
                        className="py-6 text-center italic text-gray-400"
                      >
                        No medicines added yet.
                      </td>
                    </tr>
                  ) : (
                    medicines.map((med, idx) => (
                      <tr
                        key={idx}
                        className={idx % 2 === 0 ? "bg-white" : "bg-theme-50"}
                      >
                        <td className="px-3 py-2 whitespace-nowrap">{med.medicine_name}</td>
                        <td className="px-3 py-2 whitespace-nowrap">{med.brand_name}</td>
                        <td className="px-3 py-2 whitespace-nowrap">{med.mfg_date}</td>
                        <td className="px-3 py-2 whitespace-nowrap">{med.expiry_date}</td>
                        <td className="px-3 py-2 whitespace-nowrap">{med.packed_type}</td>
                        <td className="px-3 py-2 whitespace-nowrap">{med.stock_quantity}</td>
                        <td className="px-3 py-2 whitespace-nowrap">{med.purchase_price}</td>
                        <td className="px-3 py-2 whitespace-nowrap">{med.mrp}</td>
                        <td className="px-3 py-2 whitespace-nowrap">{med.batch_no}</td>
                        <td className="px-3 py-2">
                          <button
                            type="button"
                            className="text-red-600 hover:underline"
                            onClick={() => handleDeleteMedicine(idx)}
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {/* Messages */}
        {error && (
          <div className="p-4 mb-4 text-sm text-red-800 bg-red-100 rounded-lg" role="alert">
            {error}
          </div>
        )}
        {message && (
          <div className="p-4 mb-4 text-sm text-green-800 bg-green-100 rounded-lg" role="alert">
            {message}
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex flex-col items-center justify-end gap-3 sm:flex-row">
          <button
            type="button"
            onClick={() => {
              setWholesaler("");
              setInvoiceNumber("");
              setDate("");
              setMedicines([]);
              setError(null);
              setMessage(null);
            }}
            className="w-full px-6 py-2 font-medium text-gray-700 bg-gray-200 rounded-md sm:w-auto hover:bg-gray-300"
            disabled={loading}
          >
            Cancel
          </button>
          <button
            type="submit"
            className="w-full px-6 py-2 font-medium text-white bg-indigo-600 rounded-md sm:w-auto hover:bg-indigo-700"
            disabled={loading}
          >
            {loading ? "Submitting..." : "Submit Entry"}
          </button>
        </div>
      </form>

      {/* Add Medicine Modal */}
      {showAddMedicineModal && (
        <AddMedicineModal
          onClose={() => setShowAddMedicineModal(false)}
          onAddMedicine={handleAddMedicine}
        />
      )}
    </div>
  );
}

export default StockEntry;
