import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  LayoutDashboard, 
  Package, 
  BarChart3, 
  FileText, 
  DollarSign, 
  CheckSquare, 
  Users, 
  Settings,
  Menu,
  X,
  User
} from 'lucide-react';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [activeMenu, setActiveMenu] = useState('Dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const menuItems = [
    { name: 'Dashboard', icon: LayoutDashboard },
    { name: 'Add Stock', icon: Package },
    { name: 'Stock', icon: BarChart3 },
    { name: 'Report', icon: FileText },
    { name: 'FinTrack', icon: DollarSign },
    { name: 'Todo', icon: CheckSquare },
    { name: 'Staff', icon: Users },
    { name: 'Setting', icon: Settings },
  ];

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const handleMenuClick = (menuName) => {
    setActiveMenu(menuName);
    setSidebarOpen(false); // Close sidebar on mobile after selection
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Top Bar (Header) */}
      <header className="fixed top-0 left-0 right-0 z-50 bg-white shadow-sm border-b border-gray-200">
        <div className="flex items-center justify-between p-6">
          {/* Mobile Menu Button */}
          <button
            onClick={toggleSidebar}
            className="lg:hidden p-2 rounded-md text-gray-600 hover:text-gray-900 hover:bg-gray-100"
          >
            {sidebarOpen ? <X size={24} /> : <Menu size={24} />}
          </button>

          {/* Page Title */}
          <h1 className="text-xl font-semibold text-gray-700">
            {activeMenu}
          </h1>

          {/* Profile Button */}
          <div className="flex items-center">
            <button 
              onClick={() => navigate('/profile')}
              className="w-10 h-10 bg-aqua-blue-500 rounded-full flex items-center justify-center text-white hover:bg-aqua-blue-600 transition-colors duration-200 shadow-sm"
            >
              <User size={20} />
            </button>
          </div>
        </div>
      </header>

      {/* Sidebar */}
      <aside className={`
        fixed top-0 left-0 z-40 h-full bg-sky-100 w-64 transform transition-transform duration-300 ease-in-out
        ${sidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'}
        lg:top-16 lg:z-30
      `}>
        <div className="flex flex-col h-full">
          {/* Logo and App Name */}
          <div className="p-6 border-b border-sky-200">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-aqua-blue-500 rounded-lg flex items-center justify-center">
                <Package size={20} className="text-white" />
              </div>
              <span className="text-lg font-bold text-gray-800">pharmaDesk</span>
            </div>
          </div>

          {/* Menu Items */}
          <nav className="flex-1 p-4 space-y-2">
            {menuItems.map((item) => {
              const IconComponent = item.icon;
              const isActive = activeMenu === item.name;
              
              return (
                <button
                  key={item.name}
                  onClick={() => handleMenuClick(item.name)}
                  className={`
                    w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-left transition-colors duration-200
                    ${isActive 
                      ? 'bg-aqua-blue-500 text-white font-semibold' 
                      : 'text-gray-700 hover:bg-sky-200 hover:text-gray-900'
                    }
                  `}
                >
                  <IconComponent size={20} />
                  <span>{item.name}</span>
                </button>
              );
            })}
          </nav>
        </div>
      </aside>

      {/* Overlay for mobile */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-30 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main Content Area */}
      <main className={`
        transition-all duration-300 ease-in-out
        ${sidebarOpen ? 'lg:ml-64' : 'lg:ml-64'}
        pt-20 lg:pt-20
      `}>
        <div className="p-6">
          {/* Content based on active menu */}
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">
              {activeMenu} Content
            </h2>
            <p className="text-gray-600">
              This is the content area for {activeMenu}. The layout adjusts based on the sidebar state and screen size.
            </p>
            
            {/* Sample content for different sections */}
            {activeMenu === 'Dashboard' && (
              <div className="mt-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h3 className="font-semibold text-blue-800">Total Stock</h3>
                  <p className="text-2xl font-bold text-blue-600">1,234</p>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <h3 className="font-semibold text-green-800">Sales Today</h3>
                  <p className="text-2xl font-bold text-green-600">$5,678</p>
                </div>
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <h3 className="font-semibold text-yellow-800">Pending Orders</h3>
                  <p className="text-2xl font-bold text-yellow-600">23</p>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h3 className="font-semibold text-purple-800">Staff Online</h3>
                  <p className="text-2xl font-bold text-purple-600">8</p>
                </div>
              </div>
            )}

            {activeMenu === 'Add Stock' && (
              <div className="mt-6">
                <p className="text-gray-600 mb-4">Add new stock items to your inventory.</p>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Stock management form will be implemented here.</p>
                </div>
              </div>
            )}

            {activeMenu === 'Stock' && (
              <div className="mt-6">
                <p className="text-gray-600 mb-4">View and manage your current stock levels.</p>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Stock inventory table will be implemented here.</p>
                </div>
              </div>
            )}

            {activeMenu === 'Report' && (
              <div className="mt-6">
                <p className="text-gray-600 mb-4">Generate and view detailed reports.</p>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Report generation interface will be implemented here.</p>
                </div>
              </div>
            )}

            {activeMenu === 'FinTrack' && (
              <div className="mt-6">
                <p className="text-gray-600 mb-4">Track your financial data and transactions.</p>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Financial tracking dashboard will be implemented here.</p>
                </div>
              </div>
            )}

            {activeMenu === 'Todo' && (
              <div className="mt-6">
                <p className="text-gray-600 mb-4">Manage your tasks and to-do items.</p>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Todo list interface will be implemented here.</p>
                </div>
              </div>
            )}

            {activeMenu === 'Staff' && (
              <div className="mt-6">
                <p className="text-gray-600 mb-4">Manage your staff and team members.</p>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Staff management interface will be implemented here.</p>
                </div>
              </div>
            )}

            {activeMenu === 'Setting' && (
              <div className="mt-6">
                <p className="text-gray-600 mb-4">Configure your application settings.</p>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Settings interface will be implemented here.</p>
                </div>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
