// src/components/AddMedicineModal.js
import React, { useState, useRef, useEffect, useCallback } from "react";
const  BASE_URL  = "http://localhost:4000";

function formatDate(dateStr) {
  if (!dateStr) return "";
  // Handles both ISO and YYYY-MM-DD
  return dateStr.slice(0, 10);
}

const initialFields = {
  medicine_name: "",
  brand_name: "",
  mfg_date: "",
  expiry_date: "",
  packed_type: "",
  stock_quantity: "",
  purchase_price: "",
  mrp: "",
  batch_no: "",
};

export default function AddMedicineModal({ onClose, onAddMedicine }) {
  const [fields, setFields] = useState(initialFields);
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [selectedIdx, setSelectedIdx] = useState(-1);
  const [loading, setLoading] = useState(false);
  const [fetchingDetails, setFetchingDetails] = useState(false);
  const [error, setError] = useState(null);

  const debounceTimeout = useRef(null);
  const inputRef = useRef(null);

  // Debounced fetch for medicine name suggestions
  useEffect(() => {
    if (!fields.medicine_name) {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }
    setShowSuggestions(true);
    if (debounceTimeout.current) clearTimeout(debounceTimeout.current);
    debounceTimeout.current = setTimeout(async () => {
      setLoading(true);
      try {
        const res = await fetch(
          `${BASE_URL}/admin/medicines/recommendation?query=${encodeURIComponent(
            fields.medicine_name
          )}`
        );
        const data = await res.json();
        if (data.status === "success") {
          setSuggestions(data.recommendations || []);
        } else {
          setSuggestions([]);
        }
      } catch {
        setSuggestions([]);
      }
      setLoading(false);
    }, 400);
    // Cleanup
    return () => clearTimeout(debounceTimeout.current);
    // eslint-disable-next-line
  }, [fields.medicine_name]);

  // Keyboard navigation for suggestions
  const handleKeyDown = (e) => {
    if (!showSuggestions || suggestions.length === 0) return;
    if (e.key === "ArrowDown") {
      e.preventDefault();
      setSelectedIdx((idx) => (idx + 1) % suggestions.length);
    } else if (e.key === "ArrowUp") {
      e.preventDefault();
      setSelectedIdx((idx) =>
        idx <= 0 ? suggestions.length - 1 : idx - 1
      );
    } else if (e.key === "Enter" && selectedIdx >= 0) {
      e.preventDefault();
      handleSelectSuggestion(suggestions[selectedIdx]);
    }
  };

  // Select suggestion and fetch details
  const handleSelectSuggestion = async (suggestion) => {
    setShowSuggestions(false);
    setFields((f) => ({
      ...f,
      medicine_name: suggestion.medicine_name,
    }));
    setSuggestions([]);
    setSelectedIdx(-1);
    setFetchingDetails(true);
    try {
      const res = await fetch(
        `${BASE_URL}/admin/medicine/${suggestion.medicine_id}`
      );
      const data = await res.json();
      if (
        data.status === "success" &&
        data.medicine &&
        data.medicine.length > 0
      ) {
        const med = data.medicine[0];
        setFields({
          medicine_name: med.medicine_name || "",
          brand_name: med.brand_name || "",
          mfg_date: formatDate(med.mfg_date),
          expiry_date: formatDate(med.expiry_date),
          packed_type: med.packed_type || "",
          stock_quantity: med.stock_quantity || "",
          purchase_price: med.purchase_price || "",
          mrp: med.mrp || "",
          batch_no: med.batch_no || "",
        });
      }
    } catch {}
    setFetchingDetails(false);
  };

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFields((f) => ({
      ...f,
      [name]: value,
    }));
    if (name === "medicine_name") {
      setShowSuggestions(true);
      setSelectedIdx(-1);
    }
  };

  // Add medicine
  const handleAdd = () => {
    setError(null);
    // Basic validation
    if (
      !fields.medicine_name ||
      !fields.brand_name ||
      !fields.mfg_date ||
      !fields.expiry_date ||
      !fields.packed_type ||
      !fields.stock_quantity ||
      !fields.purchase_price ||
      !fields.mrp ||
      !fields.batch_no
    ) {
      setError("Please fill all fields.");
      return;
    }
    // Pass medicine object to parent
    onAddMedicine({
      medicine_name: fields.medicine_name,
      brand_name: fields.brand_name,
      mfg_date: fields.mfg_date,
      expiry_date: fields.expiry_date,
      packed_type: fields.packed_type,
      stock_quantity: Number(fields.stock_quantity),
      purchase_price: fields.purchase_price,
      mrp: fields.mrp,
      batch_no: fields.batch_no,
    });
    onClose();
  };

  // Refresh/clear fields
  const handleRefresh = () => {
    setFields(initialFields);
    setSuggestions([]);
    setShowSuggestions(false);
    setSelectedIdx(-1);
    setError(null);
  };

  // Click outside to close suggestions
  useEffect(() => {
    const handleClick = (e) => {
      if (
        inputRef.current &&
        !inputRef.current.contains(e.target)
      ) {
        setShowSuggestions(false);
      }
    };
    document.addEventListener("mousedown", handleClick);
    return () => document.removeEventListener("mousedown", handleClick);
  }, []);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-30">
      <div className="bg-white rounded-lg shadow-lg w-full max-w-lg p-6 relative">
        {/* Header */}
        <div className="flex justify-between items-center mb-2">
          <h2 className="text-xl font-bold">Add New Medicine</h2>
          <button
            className="text-gray-500 hover:text-gray-700 text-2xl"
            onClick={onClose}
            title="Close"
          >
            ×
          </button>
        </div>
        <p className="mb-4 text-gray-600">
          Enter details for the medicine to add to stock.
        </p>
        {/* Form */}
        <div className="space-y-3">
          {/* Medicine Name with auto-suggest */}
          <div ref={inputRef} className="relative">
            <label className="block font-medium mb-1">
              Medicine Name <span className="text-red-500">*</span>
            </label>
            <input
              name="medicine_name"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.medicine_name}
              onChange={handleChange}
              onKeyDown={handleKeyDown}
              autoComplete="off"
              required
            />
            {/* Suggestions */}
            {showSuggestions && fields.medicine_name && (
              <ul className="absolute z-10 left-0 right-0 bg-white border rounded shadow mt-1 max-h-40 overflow-y-auto">
                {loading ? (
                  <li className="px-3 py-2 text-gray-400">Loading...</li>
                ) : suggestions.length === 0 ? (
                  <li className="px-3 py-2 text-gray-400">No suggestions</li>
                ) : (
                  suggestions.map((s, idx) => (
                    <li
                      key={s.medicine_id}
                      className={`px-3 py-2 cursor-pointer ${
                        idx === selectedIdx
                          ? "bg-blue-100"
                          : "hover:bg-gray-100"
                      }`}
                      onMouseDown={() => handleSelectSuggestion(s)}
                    >
                      {s.medicine_name}
                    </li>
                  ))
                )}
              </ul>
            )}
          </div>
          {/* Brand */}
          <div>
            <label className="block font-medium mb-1">
              Brand <span className="text-red-500">*</span>
            </label>
            <input
              name="brand_name"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.brand_name}
              onChange={handleChange}
              required
            />
          </div>
          {/* MFG Date */}
          <div>
            <label className="block font-medium mb-1">
              MFG Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              name="mfg_date"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.mfg_date}
              onChange={handleChange}
              required
            />
          </div>
          {/* Expiry Date */}
          <div>
            <label className="block font-medium mb-1">
              Expiry Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              name="expiry_date"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.expiry_date}
              onChange={handleChange}
              required
            />
          </div>
          {/* Packed Type */}
          <div>
            <label className="block font-medium mb-1">
              Packed Type <span className="text-red-500">*</span>
            </label>
            <input
              name="packed_type"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.packed_type}
              onChange={handleChange}
              required
            />
          </div>
          {/* Quantity */}
          <div>
            <label className="block font-medium mb-1">
              Quantity <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              name="stock_quantity"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.stock_quantity}
              onChange={handleChange}
              min={1}
              required
            />
          </div>
          {/* Purchase Price */}
          <div>
            <label className="block font-medium mb-1">
              Purchase Price (₹) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              name="purchase_price"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.purchase_price}
              onChange={handleChange}
              min={0}
              step="0.01"
              required
            />
          </div>
          {/* MRP */}
          <div>
            <label className="block font-medium mb-1">
              MRP (₹) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              name="mrp"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.mrp}
              onChange={handleChange}
              min={0}
              step="0.01"
              required
            />
          </div>
          {/* Batch No */}
          <div>
            <label className="block font-medium mb-1">
              Batch No <span className="text-red-500">*</span>
            </label>
            <input
              name="batch_no"
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring"
              value={fields.batch_no}
              onChange={handleChange}
              required
            />
          </div>
        </div>
        {/* Error */}
        {error && (
          <div className="mt-2 text-red-600 font-medium">{error}</div>
        )}
        {/* Buttons */}
        <div className="flex justify-end space-x-2 mt-6">
          <button
            type="button"
            className="px-4 py-2 rounded bg-gray-200 hover:bg-gray-300"
            onClick={onClose}
            disabled={fetchingDetails}
          >
            Cancel
          </button>
          <button
            type="button"
            className="px-4 py-2 rounded bg-yellow-400 hover:bg-yellow-500 text-white"
            onClick={handleRefresh}
            disabled={fetchingDetails}
          >
            Refresh
          </button>
          <button
            type="button"
            className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700"
            onClick={handleAdd}
            disabled={fetchingDetails}
          >
            {fetchingDetails ? "Loading..." : "Add"}
          </button>
        </div>
      </div>
    </div>
  );
}
