import React, { useState, useEffect, useRef, useCallback } from 'react';
import { 
  Search, 
  Filter, 
  Eye, 
  ChevronDown, 
  ChevronUp,
  ArrowLeft,
  ArrowRight,
  Package,
  AlertTriangle,
  CheckCircle,
  Clock,
  Calendar,
  DollarSign,
  Building,
  Pill,
  X,
  ChevronRight
} from 'lucide-react';
import axios from 'axios';

const Stock = () => {
  // State management
  const [stockData, setStockData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchType, setSearchType] = useState('Medicine');
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('quantity-high');
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const [selectedSuggestionIndex, setSelectedSuggestionIndex] = useState(-1);
  const [showModal, setShowModal] = useState(false);
  const [selectedMedicine, setSelectedMedicine] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [message, setMessage] = useState('');

  const searchRef = useRef(null);

  // Mock data for demonstration
  const mockStockData = [
    {
      id: 1,
      medicineName: 'Paracetamol 500mg',
      brand: 'Dolo',
      batchNo: 'BATCH001',
      invoiceNo: 'INV2024001',
      quantity: 150,
      expiryDate: '2024-12-31',
      price: 5.50,
      mrp: 8.00,
      purchasePrice: 4.50,
      packedType: 'Strip',
      wholesaler: 'MedPlus Distributors',
      manufacturingDate: '2024-01-15',
      createdAt: '2024-01-20T10:30:00Z',
      currentStock: 150
    },
    {
      id: 2,
      medicineName: 'Amoxicillin 250mg',
      brand: 'Novamox',
      batchNo: 'BATCH002',
      invoiceNo: 'INV2024002',
      quantity: 75,
      expiryDate: '2024-06-15',
      price: 12.00,
      mrp: 15.00,
      purchasePrice: 10.00,
      packedType: 'Capsule',
      wholesaler: 'PharmaCare Ltd',
      manufacturingDate: '2024-01-10',
      createdAt: '2024-01-18T14:20:00Z',
      currentStock: 75
    },
    {
      id: 3,
      medicineName: 'Omeprazole 20mg',
      brand: 'Omez',
      batchNo: 'BATCH003',
      invoiceNo: 'INV2024003',
      quantity: 25,
      expiryDate: '2024-03-20',
      price: 25.00,
      mrp: 30.00,
      purchasePrice: 22.00,
      packedType: 'Tablet',
      wholesaler: 'MedPlus Distributors',
      manufacturingDate: '2024-01-05',
      createdAt: '2024-01-15T09:15:00Z',
      currentStock: 25
    },
    {
      id: 4,
      medicineName: 'Cetirizine 10mg',
      brand: 'Alerid',
      batchNo: 'BATCH004',
      invoiceNo: 'INV2024004',
      quantity: 200,
      expiryDate: '2025-02-28',
      price: 8.00,
      mrp: 12.00,
      purchasePrice: 6.50,
      packedType: 'Tablet',
      wholesaler: 'HealthCare Solutions',
      manufacturingDate: '2024-01-20',
      createdAt: '2024-01-22T16:45:00Z',
      currentStock: 200
    },
    {
      id: 5,
      medicineName: 'Metformin 500mg',
      brand: 'Glycomet',
      batchNo: 'BATCH005',
      invoiceNo: 'INV2024005',
      quantity: 50,
      expiryDate: '2024-08-10',
      price: 18.00,
      mrp: 22.00,
      purchasePrice: 15.00,
      packedType: 'Tablet',
      wholesaler: 'PharmaCare Ltd',
      manufacturingDate: '2024-01-12',
      createdAt: '2024-01-19T11:30:00Z',
      currentStock: 50
    }
  ];

  // Fetch stock data on component mount
  useEffect(() => {
    fetchStockData();
  }, []);

  // Filter and sort data when dependencies change
  useEffect(() => {
    filterAndSortData();
  }, [stockData, searchQuery, sortBy]);

  const fetchStockData = async () => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        window.location.href = '/login';
        return;
      }

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setStockData(mockStockData);
      setFilteredData(mockStockData);
    } catch (error) {
      console.error('Error fetching stock data:', error);
      setMessage('Failed to load stock data');
    } finally {
      setIsLoading(false);
    }
  };

  const filterAndSortData = () => {
    let filtered = [...stockData];

    // Apply search filter
    if (searchQuery) {
      filtered = filtered.filter(item => {
        if (searchType === 'Medicine') {
          return item.medicineName.toLowerCase().includes(searchQuery.toLowerCase()) ||
                 item.brand.toLowerCase().includes(searchQuery.toLowerCase());
        } else {
          return item.wholesaler.toLowerCase().includes(searchQuery.toLowerCase());
        }
      });
    }

    // Apply sorting
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'quantity-high':
          return b.quantity - a.quantity;
        case 'quantity-low':
          return a.quantity - b.quantity;
        case 'expiry-soon':
          return new Date(a.expiryDate) - new Date(b.expiryDate);
        case 'price-high':
          return b.price - a.price;
        case 'price-low':
          return a.price - b.price;
        default:
          return 0;
      }
    });

    setFilteredData(filtered);
    setCurrentPage(1); // Reset to first page when filtering
  };

  const getQuantityBadge = (quantity) => {
    if (quantity >= 100) {
      return <span className="px-2 py-1 text-xs font-medium bg-green-100 text-green-800 rounded-full">High Stock</span>;
    } else if (quantity >= 50) {
      return <span className="px-2 py-1 text-xs font-medium bg-yellow-100 text-yellow-800 rounded-full">Medium</span>;
    } else {
      return <span className="px-2 py-1 text-xs font-medium bg-red-100 text-red-800 rounded-full">Low</span>;
    }
  };

  const getExpiryStatus = (expiryDate) => {
    const today = new Date();
    const expiry = new Date(expiryDate);
    const diffTime = expiry - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays < 0) {
      return <span className="text-red-600 font-medium">Expired</span>;
    } else if (diffDays <= 30) {
      return <span className="text-orange-600 font-medium">in {diffDays} days</span>;
    } else {
      const months = Math.floor(diffDays / 30);
      return <span className="text-green-600 font-medium">in {months} month{months > 1 ? 's' : ''}</span>;
    }
  };

  const handleViewMedicine = (medicine) => {
    setSelectedMedicine(medicine);
    setShowModal(true);
  };

  // Pagination
  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentData = filteredData.slice(startIndex, endIndex);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-aqua-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading stock data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      {/* Breadcrumb */}
      <nav className="mb-6">
        <ol className="flex items-center space-x-2 text-sm text-gray-600">
          <li className="hover:text-aqua-blue-600 cursor-pointer">PharmaDesk</li>
          <ChevronRight size={16} />
          <li className="text-aqua-blue-600 font-medium">Stock Management</li>
        </ol>
      </nav>

      {/* Header */}
      <div className="mb-6">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Stock Management</h1>
        <p className="text-gray-600">Manage your pharmacy inventory efficiently</p>
      </div>

      {/* Search & Filter Bar */}
      <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {/* Search Type Dropdown */}
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-2">Search Type</label>
            <select
              value={searchType}
              onChange={(e) => setSearchType(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-aqua-blue-500 focus:border-transparent"
            >
              <option value="Medicine">Medicine</option>
              <option value="Wholesaler">Wholesaler</option>
            </select>
          </div>

          {/* Search Input */}
          <div className="relative md:col-span-2">
            <label className="block text-sm font-medium text-gray-700 mb-2">Search</label>
            <div className="relative">
              <Search className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder={`Search by ${searchType.toLowerCase()}...`}
                className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-aqua-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          {/* Sort Dropdown */}
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-2">Sort By</label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-aqua-blue-500 focus:border-transparent"
            >
              <option value="quantity-high">Quantity: High to Low</option>
              <option value="quantity-low">Quantity: Low to High</option>
              <option value="expiry-soon">Expiry: Soonest First</option>
              <option value="price-high">Price: High to Low</option>
              <option value="price-low">Price: Low to High</option>
            </select>
          </div>
        </div>
      </div>

      {/* Stock Table */}
      <div className="bg-white rounded-lg shadow-sm overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">S.No</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Medicine</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Brand</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Batch No</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Invoice No</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Quantity</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Expiry</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Action</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {currentData.map((item, index) => (
                <tr key={item.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {startIndex + index + 1}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-semibold text-gray-900">{item.medicineName}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {item.brand}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {item.batchNo}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {item.invoiceNo}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center space-x-2">
                      <span className="text-sm text-gray-900">{item.quantity}</span>
                      {getQuantityBadge(item.quantity)}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {getExpiryStatus(item.expiryDate)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    ₹{item.price.toFixed(2)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <button
                      onClick={() => handleViewMedicine(item)}
                      className="flex items-center space-x-1 text-aqua-blue-600 hover:text-aqua-blue-900 transition-colors duration-200"
                    >
                      <Eye size={16} />
                      <span>View</span>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="bg-white px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
            <div className="flex-1 flex justify-between sm:hidden">
              <button
                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
                className="relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <button
                onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
                className="ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </div>
            <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
              <div>
                <p className="text-sm text-gray-700">
                  Showing <span className="font-medium">{startIndex + 1}</span> to{' '}
                  <span className="font-medium">{Math.min(endIndex, filteredData.length)}</span> of{' '}
                  <span className="font-medium">{filteredData.length}</span> results
                </p>
              </div>
              <div>
                <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px">
                  <button
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                    disabled={currentPage === 1}
                    className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <ArrowLeft size={16} />
                  </button>
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                    <button
                      key={page}
                      onClick={() => setCurrentPage(page)}
                      className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                        page === currentPage
                          ? 'z-10 bg-aqua-blue-50 border-aqua-blue-500 text-aqua-blue-600'
                          : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                      }`}
                    >
                      {page}
                    </button>
                  ))}
                  <button
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                    disabled={currentPage === totalPages}
                    className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <ArrowRight size={16} />
                  </button>
                </nav>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Medicine Details Modal */}
      {showModal && selectedMedicine && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex items-center justify-between mb-6">
                <h3 className="text-lg font-semibold text-gray-900">Medicine Details</h3>
                <button
                  onClick={() => setShowModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X size={20} />
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Medicine Name</label>
                  <p className="text-sm text-gray-900 font-medium">{selectedMedicine.medicineName}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Brand</label>
                  <p className="text-sm text-gray-900">{selectedMedicine.brand}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Batch No</label>
                  <p className="text-sm text-gray-900">{selectedMedicine.batchNo}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Invoice No</label>
                  <p className="text-sm text-gray-900">{selectedMedicine.invoiceNo}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Quantity</label>
                  <p className="text-sm text-gray-900">{selectedMedicine.quantity} units</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">MRP</label>
                  <p className="text-sm text-gray-900">₹{selectedMedicine.mrp.toFixed(2)}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Purchase Price</label>
                  <p className="text-sm text-gray-900">₹{selectedMedicine.purchasePrice.toFixed(2)}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Expiry Date</label>
                  <p className="text-sm text-gray-900">{new Date(selectedMedicine.expiryDate).toLocaleDateString('en-GB')}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Packed Type</label>
                  <p className="text-sm text-gray-900">{selectedMedicine.packedType}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Wholesaler</label>
                  <p className="text-sm text-gray-900">{selectedMedicine.wholesaler}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Manufacturing Date</label>
                  <p className="text-sm text-gray-900">{new Date(selectedMedicine.manufacturingDate).toLocaleDateString('en-GB')}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Created At</label>
                  <p className="text-sm text-gray-900">{new Date(selectedMedicine.createdAt).toLocaleString('en-GB')}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Current Stock</label>
                  <p className="text-sm text-gray-900">{selectedMedicine.currentStock} units</p>
                </div>
              </div>

              <div className="flex space-x-3 pt-4 border-t border-gray-200">
                <button
                  onClick={() => {
                    setMessage('Purchase return functionality will be implemented');
                    setShowModal(false);
                  }}
                  className="flex-1 bg-orange-500 text-white px-4 py-2 rounded-lg hover:bg-orange-600 transition-colors duration-200"
                >
                  Purchase Return
                </button>
                <button
                  onClick={() => {
                    setMessage('Update functionality will be implemented');
                    setShowModal(false);
                  }}
                  className="flex-1 bg-aqua-blue-500 text-white px-4 py-2 rounded-lg hover:bg-aqua-blue-600 transition-colors duration-200"
                >
                  Update
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Message Display */}
      {message && (
        <div className="mt-4 p-3 rounded-lg text-sm bg-blue-100 text-blue-700">
          {message}
        </div>
      )}
    </div>
  );
};

export default Stock;
