import React, { useState, useEffect, useMemo } from 'react';
import { Search, ArrowLeft, X, ChevronLeft, ChevronRight, Trash2, Plus, FileSpreadsheet } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import BoxLoader from '../../looader/BoxLoader';

// Assuming you have a file for toast notifications (e.g., react-hot-toast, react-toastify)
// and a utility for managing your bearer token.
// import { toast } from 'react-hot-toast'; 

// --- API CONFIGURATION ---
const API_URL = 'http://localhost:4000'; // Replace with your actual backend URL

// Function to get the auth token from local storage or context
const getAuthToken = () => {
    // You should replace this with your actual method of getting the token
    return localStorage.getItem('lalitkumar_choudhary'); 
};



const AddWholesalerModal = ({ isOpen, onClose, onAddWholesaler, isLoading }) => {
    const [formData, setFormData] = useState({
        name: '',
        gst_no: '',
        address: '',
        contact: '',
        email: '',
    });

    if (!isOpen) return null;

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({ ...prevData, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddWholesaler(formData);
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4">
            <div className="bg-white rounded-lg shadow-xl w-full max-w-lg max-h-[90vh] overflow-y-auto font-sans">
                {isLoading && (
                    <div className="absolute inset-0 flex items-center justify-center bg-white bg-opacity-75 z-20">
                        <BoxLoader />
                    </div>
                )}
                <div className={`${isLoading ? 'blur-sm' : ''}`}>
                    <div className="p-6 border-b sticky top-0 bg-white z-10">
                        <div className="flex justify-between items-center">
                            <h2 className="text-xl font-semibold text-gray-800">Add New Wholesaler</h2>
                            <button onClick={onClose} className="text-gray-500 hover:text-gray-800">
                                <X size={24} />
                            </button>
                        </div>
                    </div>
                    <form onSubmit={handleSubmit} className="p-6 space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700">Name</label>
                            <input
                                type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                required
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-theme-500 focus:ring-theme-500 sm:text-xl"
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700">GST No</label>
                            <input
                                type="text"
                                name="gst_no"
                                value={formData.gst_no}
                                onChange={handleChange}
                                required
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-theme-500 focus:ring-theme-500 sm:text-xl"
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700">Address</label>
                            <input
                                type="text"
                                name="address"
                                value={formData.address}
                                onChange={handleChange}
                                required
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-theme-500 focus:ring-theme-500 sm:text-xl"
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700">Contact No</label>
                            <input
                                type="tel"
                                name="contact"
                                value={formData.contact}
                                onChange={handleChange}
                                required
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-theme-500 focus:ring-theme-500 sm:text-xl"
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700">Email</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-theme-500 focus:ring-theme-500 sm:text-xl"
                            />
                        </div>
                        <div className="flex justify-end">
                            <button
                                type="submit"
                                className="flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-700 transition-colors"
                            >
                                Add Wholesaler
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

// --- MAIN APP COMPONENT ---
function Wholesaler() {
    const [wholesalers, setWholesalers] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortOption, setSortOption] = useState('Stock Import High to Low');
    const [selectedWholesaler, setSelectedWholesaler] = useState(null);
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [isLoading, setIsLoading] = useState(false);
    const [wholesalerDetailsLoading, setWholesalerDetailsLoading] = useState(false);
    const [addWholesalerLoading, setAddWholesalerLoading] = useState(false);
    const [wholesalerDetailsError, setWholesalerDetailsError] = useState(null);
    const itemsPerPage = 5;

    const navigate = useNavigate();

    const fetchWholesalers = async () => {
        setIsLoading(true);
        try {
            const token = getAuthToken();
            const response = await axios.get(`${API_URL}/admin/Wholesalers`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            // The API response has a 'rows' key containing the list of wholesalers
            setWholesalers(response.data.rows);
            // toast.success('Wholesalers loaded successfully!');
        } catch (error) {
            console.error('Error fetching wholesalers:', error);
            // toast.error('Failed to load wholesalers.');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchWholesalers();
    }, []);

    const fetchWholesalerDetails = async (wholesaler_id) => {
        setWholesalerDetailsLoading(true);
        setWholesalerDetailsError(null);
        try {
            const token = getAuthToken();
            const response = await axios.get(`${API_URL}/admin/wholesaler/${wholesaler_id}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            // The API response has a 'wholesaler' key
            setSelectedWholesaler(response.data.wholesaler);
        } catch (error) {
            console.error('Error fetching wholesaler details:', error);
            setWholesalerDetailsError('Failed to fetch wholesaler details. Please try again.');
            setSelectedWholesaler(null);
        } finally {
            setWholesalerDetailsLoading(false);
        }
    };

    const handleViewDetails = (wholesalerId) => {
        // Set the selected wholesaler to null first to clear previous data
        // This is important to ensure the modal shows a loader for the new request
        setSelectedWholesaler(null); 
        fetchWholesalerDetails(wholesalerId);
    };

    const handleCloseModal = () => {
        setSelectedWholesaler(null);
        setWholesalerDetailsError(null);
    };

    const handleOpenAddModal = () => {
        setIsAddModalOpen(true);
    };

    const handleCloseAddModal = () => {
        setIsAddModalOpen(false);
    };

    const handleAddWholesaler = async (newWholesalerData) => {
        setAddWholesalerLoading(true);
        try {
            const token = getAuthToken();
            await axios.post(`${API_URL}/admin/Wholesaler`, newWholesalerData, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            // toast.success('Wholesaler added successfully!');
            handleCloseAddModal();
            fetchWholesalers(); // Re-fetch the list to show the new wholesaler
        } catch (error) {
            console.error('Error adding new wholesaler:', error);
            // toast.error('Failed to add wholesaler.');
        } finally {
            setAddWholesalerLoading(false);
        }
    };

    const handleDeleteWholesaler = async (wholesalerId) => {
        if (window.confirm('Are you sure you want to delete this wholesaler?')) {
            setWholesalerDetailsLoading(true);
            try {
                const token = getAuthToken();
                await axios.delete(`${API_URL}/admin/Wholesaler/${wholesalerId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                // toast.success('Wholesaler deleted successfully!');
                handleCloseModal();
                fetchWholesalers(); // Re-fetch the list
            } catch (error) {
                console.error('Error deleting wholesaler:', error);
                // toast.error('Failed to delete wholesaler.');
            } finally {
                setWholesalerDetailsLoading(false);
            }
        }
    };

    const handleGoBack = () => {
        navigate('/home/reports');
    };

    const handleExport = () => {
        alert('Export to Excel functionality coming soon!');
    };

    const filteredAndSortedWholesalers = useMemo(() => {
        let processedWholesalers = [...wholesalers];

        if (searchTerm) {
            processedWholesalers = processedWholesalers.filter(wholesaler =>
                wholesaler.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                wholesaler.gst_no.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        switch (sortOption) {
            case 'Stock Import High to Low':
                processedWholesalers.sort((a, b) => b.stockImport - a.stockImport);
                break;
            case 'Stock Import Low to High':
                processedWholesalers.sort((a, b) => a.stockImport - b.stockImport);
                break;
            case 'Unpaid Amount High to Low':
                processedWholesalers.sort((a, b) => b.unpaidAmount - a.unpaidAmount);
                break;
            case 'Unpaid Amount Low to High':
                processedWholesalers.sort((a, b) => a.unpaidAmount - b.unpaidAmount);
                break;
            default:
                break;
        }

        return processedWholesalers;
    }, [wholesalers, searchTerm, sortOption]);

    const totalItems = filteredAndSortedWholesalers.length;
    const totalPages = Math.ceil(totalItems / itemsPerPage);
    const paginatedWholesalers = filteredAndSortedWholesalers.slice(
        (currentPage - 1) * itemsPerPage,
        currentPage * itemsPerPage
    );

    const handleNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    const handlePrevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    return (
        <div className="bg-theme-70 min-h-screen overflow-y-auto font-sans">
            {isLoading && (
                <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-white/30">
                    <BoxLoader />
                </div>
            )}
            <div className={`container mx-auto p-4 md:p-8 ${isLoading ? 'blur-sm' : ''}`}>
                {/* Header */}
                <div className="mb-6 flex flex-col md:flex-row md:items-center md:justify-between gap-4">
                    <div className="flex items-center gap-2">
                        <button onClick={handleGoBack} className="p-2 rounded-full hover:bg-gray-200">
                            <ArrowLeft size={24} />
                        </button>
                        <h1 className="text-3xl font-bold text-gray-800">Wholesalers</h1>
                    </div>
                    <div className="flex flex-col md:flex-row items-center gap-4">
                        <button
                            onClick={handleExport}
                            className="w-full md:w-auto flex items-center gap-2 px-4 py-2 bg-orange-400 text-white rounded-lg hover:bg-orange-600 transition-colors"
                        >
                            <FileSpreadsheet size={20} />
                            Export to Excel
                        </button>
                        <button
                            onClick={handleOpenAddModal}
                            className="w-full md:w-auto flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-700 transition-colors"
                        >
                            <Plus size={20} />
                            Add Wholesaler
                        </button>
                    </div>
                </div>

                {/* Search and Filter Controls */}
                <div className="flex flex-col md:flex-row gap-4 mb-6">
                    <div className="relative flex-grow">
                        <Search size={20} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                        <input
                            type="text"
                            placeholder="Search by wholesaler name or GST no..."
                            value={searchTerm}
                            onChange={(e) => {
                                setSearchTerm(e.target.value);
                                setCurrentPage(1);
                            }}
                            className="w-full pl-10 pr-4 py-2.5 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-theme-500"
                        />
                    </div>
                    <div className="relative">
                        <select
                            value={sortOption}
                            onChange={(e) => {
                                setSortOption(e.target.value);
                                setCurrentPage(1);
                            }}
                            className="w-full md:w-auto appearance-none bg-white border border-gray-300 rounded-lg px-4 py-2.5 pr-8 focus:outline-none focus:ring-2 focus:ring-theme-500"
                        >
                            <option>Stock Import High to Low</option>
                            <option>Stock Import Low to High</option>
                            <option>Unpaid Amount High to Low</option>
                            <option>Unpaid Amount Low to High</option>
                        </select>
                    </div>
                </div>

                <div className="bg-white rounded-lg shadow-md overflow-hidden">
                    {/* Desktop Table View */}
                    <div className="hidden md:block">
                        <table className="w-full text-left">
                            <thead className="bg-theme-200 border-b border-theme-200">
                                <tr>
                                    <th className="p-4 text-sm font-semibold text-gray-600">S.No</th>
                                    <th className="p-4 text-sm font-semibold text-gray-600">Wholesaler Name</th>
                                    <th className="p-4 text-sm font-semibold text-gray-600">GST No</th>
                                    <th className="p-4 text-sm font-semibold text-gray-600">Stock Import</th>
                                    <th className="p-4 text-sm font-semibold text-gray-600">Unpaid Amount</th>
                                    <th className="p-4 text-sm font-semibold text-gray-600">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {paginatedWholesalers.map((wholesaler, index) => (
                                    <tr key={wholesaler.wholesaler_id} className="border-b last:border-b-0 hover:bg-gray-50">
                                        <td className="p-4 text-gray-500">{(currentPage - 1) * itemsPerPage + index + 1}</td>
                                        <td className="p-4 font-medium text-gray-800">{wholesaler.name}</td>
                                        <td className="p-4 text-gray-600">{wholesaler.gst_no}</td>
                                        <td className="p-4 font-semibold text-gray-800">₹{parseFloat(wholesaler.stockImport || 0).toLocaleString('en-IN')}</td>
                                        <td className="p-4 font-semibold text-red-600">₹{parseFloat(wholesaler.unpaidAmount || 0).toLocaleString('en-IN')}</td>
                                        <td className="p-4">
                                            <button
                                                onClick={() => handleViewDetails(wholesaler.wholesaler_id)}
                                                className="bg-theme-500 text-white px-4 py-2 rounded-lg hover:bg-theme-600 transition-colors"
                                            >
                                                View
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>

                    {/* Mobile Card View */}
                    <div className="md:hidden">
                        <div className="bg-theme-50 p-4 text-center font-semibold text-theme-800">Wholesaler List</div>
                        {paginatedWholesalers.map((wholesaler) => (
                            <div key={wholesaler.wholesaler_id} className="border-b last:border-b-0 p-4">
                                <div className="flex justify-between items-start">
                                    <div>
                                        <p className="font-bold text-lg text-gray-800">{wholesaler.name}</p>
                                        <p className="text-gray-600 text-sm">GST No: {wholesaler.gst_no}</p>
                                    </div>
                                    <button
                                        onClick={() => handleViewDetails(wholesaler.wholesaler_id)}
                                        className="bg-theme-500 text-white px-5 py-2 rounded-lg text-sm hover:bg-theme-700 transition-colors"
                                    >
                                        View
                                    </button>
                                </div>
                                <div className="flex justify-between items-center mt-4">
                                    <div>
                                        <p className="text-sm text-gray-500">Stock Import</p>
                                        <p className="font-semibold text-gray-800">₹{parseFloat(wholesaler.stockImport || 0).toLocaleString('en-IN')}</p>
                                    </div>
                                    <div>
                                        <p className="text-sm text-gray-500 text-right">Unpaid</p>
                                        <p className="font-semibold text-red-600">₹{parseFloat(wholesaler.unpaidAmount || 0).toLocaleString('en-IN')}</p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Pagination Controls */}
                    <div className="flex flex-col md:flex-row justify-between items-center p-4 bg-white border-t">
                        <p className="text-sm text-gray-600 mb-2 md:mb-0">
                            Page {currentPage} of {totalPages} ({totalItems} total items)
                        </p>
                        <div className="flex items-center gap-2">
                            <button
                                onClick={handlePrevPage}
                                disabled={currentPage === 1}
                                className="flex items-center gap-1 px-3 py-2 border rounded-lg hover:bg-theme-100 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <ChevronLeft size={16} />
                                Prev
                            </button>
                            <button
                                onClick={handleNextPage}
                                disabled={currentPage === totalPages}
                                className="flex items-center gap-1 px-3 py-2 border rounded-lg hover:bg-theme-100 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <ChevronRight size={16} />
                                Next
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {/* Modals */}
            <WholesalerDetailsModal
                wholesaler={selectedWholesaler}
                onClose={handleCloseModal}
                onDelete={handleDeleteWholesaler}
                isLoading={wholesalerDetailsLoading}
                error={wholesalerDetailsError}
            />
            <AddWholesalerModal
                isOpen={isAddModalOpen}
                onClose={handleCloseAddModal}
                onAddWholesaler={handleAddWholesaler}
                isLoading={addWholesalerLoading}
            />
        </div>
    );
}

export default Wholesaler;
