import React, { useState, useMemo } from 'react';
import { createPortal } from 'react-dom';

// These are simplified icons for demonstration, using inline SVG
const ChevronDown = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    className="h-4 w-4"
  >
    <path d="m6 9 6 6 6-6" />
  </svg>
);

const Search = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    className="h-4 w-4 text-gray-500"
  >
    <circle cx="11" cy="11" r="8" />
    <path d="m21 21-4.3-4.3" />
  </svg>
);

const Menu = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    className="h-6 w-6 text-gray-700 cursor-pointer"
  >
    <line x1="4" x2="20" y1="12" y2="12" />
    <line x1="4" x2="20" y1="6" y2="6" />
    <line x1="4" x2="20" y1="18" y2="18" />
  </svg>
);

// Mock data to simulate a database response
const MOCK_MEDICINES = [
  { id: 1, name: 'Alprazolam 0.5mg', brand: 'Xanax', batch: 'ALP026', quantity: 50, price: 55.00, expiry: '12/8/2025', mrp: 58.00, purchasePrice: 45.00, invoiceNo: 'INV-001', wholesaler: 'MedSupply Co. Ltd.', packedType: 'Strip of 10 tablets', mfgDate: '8/1/2024', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 2, name: 'Amlodipine 5mg', brand: 'Norvasc', batch: 'AML009', quantity: 95, price: 38.00, expiry: '2/8/2026', mrp: 40.00, purchasePrice: 30.00, invoiceNo: 'INV-002', wholesaler: 'PharmaDist', packedType: 'Strip of 10 tablets', mfgDate: '1/1/2025', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 3, name: 'Amoxicillin 250mg', brand: 'Amoxil', batch: 'AMX002', quantity: 75, price: 45.00, expiry: '8/15/2025', mrp: 48.00, purchasePrice: 35.00, invoiceNo: 'INV-003', wholesaler: 'MedSupply Co. Ltd.', packedType: 'Strip of 10 tablets', mfgDate: '2/10/2024', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 4, name: 'Aspirin 75mg', brand: 'Disprin', batch: 'ASP011', quantity: 250, price: 15.75, expiry: '4/15/2026', mrp: 18.00, purchasePrice: 12.00, invoiceNo: 'INV-004', wholesaler: 'PharmaDist', packedType: 'Strip of 14 tablets', mfgDate: '3/1/2025', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 5, name: 'Atorvastatin 10mg', brand: 'Lipitor', batch: 'ATO007', quantity: 65, price: 65.25, expiry: '7/18/2025', mrp: 68.00, purchasePrice: 55.00, invoiceNo: 'INV-005', wholesaler: 'PharmaSupply', packedType: 'Strip of 10 tablets', mfgDate: '6/1/2024', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 6, name: 'Cetirizine 10mg', brand: 'Zyrtec', batch: 'CET001', quantity: 120, price: 18.25, expiry: '1/20/2026', mrp: 20.00, purchasePrice: 15.00, invoiceNo: 'INV-006', wholesaler: 'MedSupply Co. Ltd.', packedType: 'Strip of 10 tablets', mfgDate: '12/1/2024', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 7, name: 'Ciprofloxacin 500mg', brand: 'Cipro', batch: 'CIP003', quantity: 80, price: 58.50, expiry: '11/30/2025', mrp: 62.00, purchasePrice: 48.00, invoiceNo: 'INV-007', wholesaler: 'PharmaDist', packedType: 'Strip of 10 tablets', mfgDate: '10/1/2024', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 8, name: 'Diazepam 5mg', brand: 'Valium', batch: 'DIA010', quantity: 45, price: 30.00, expiry: '5/1/2026', mrp: 32.00, purchasePrice: 25.00, invoiceNo: 'INV-008', wholesaler: 'PharmaSupply', packedType: 'Strip of 10 tablets', mfgDate: '4/1/2025', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 9, name: 'Enalapril 10mg', brand: 'Vasotec', batch: 'ENA005', quantity: 150, price: 25.50, expiry: '9/1/2025', mrp: 28.00, purchasePrice: 20.00, invoiceNo: 'INV-009', wholesaler: 'MedSupply Co. Ltd.', packedType: 'Strip of 10 tablets', mfgDate: '8/1/2024', createdAt: '8/2/2025 9:33:19 PM' },
  { id: 10, name: 'Fluoxetine 20mg', brand: 'Prozac', batch: 'FLU015', quantity: 70, price: 75.00, expiry: '3/20/2026', mrp: 78.00, purchasePrice: 65.00, invoiceNo: 'INV-010', wholesaler: 'PharmaDist', packedType: 'Strip of 10 tablets', mfgDate: '2/1/2025', createdAt: '8/2/2025 9:33:19 PM' },
];

const Modal = ({ show, onClose, medicine }) => {
  if (!show) {
    return null;
  }

  return createPortal(
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 transition-opacity">
      <div className="bg-white rounded-xl shadow-2xl p-6 w-full max-w-lg mx-4 md:mx-auto animate-fade-in-up">
        {/* Modal Header */}
        <div className="flex items-center justify-between border-b pb-4 mb-4">
          <h3 className="text-xl font-bold text-gray-800">Medicine Details</h3>
          <button onClick={onClose} className="p-2 rounded-full text-gray-400 hover:bg-gray-100 hover:text-gray-600 transition-colors">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="h-5 w-5"
            >
              <line x1="18" y1="6" x2="6" y2="18" />
              <line x1="6" y1="6" x2="18" y2="18" />
            </svg>
          </button>
        </div>

        {/* Modal Body */}
        <div className="grid grid-cols-2 gap-x-4 gap-y-4 text-sm text-gray-600">
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">Medicine Name</span>
            <span>{medicine.name}</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">Brand</span>
            <span>{medicine.brand}</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">Batch No</span>
            <span>{medicine.batch}</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">Quantity</span>
            <span>{medicine.quantity} units</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">MRP</span>
            <span>₹{medicine.mrp.toFixed(2)}</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">Purchase Price</span>
            <span>₹{medicine.purchasePrice.toFixed(2)}</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">Invoice No</span>
            <span>{medicine.invoiceNo}</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">Expiry Date</span>
            <span>{medicine.expiry}</span>
          </div>
          <div className="flex flex-col col-span-2">
            <span className="font-semibold text-gray-800">Wholesaler</span>
            <span>{medicine.wholesaler}</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">Packed Type</span>
            <span>{medicine.packedType}</span>
          </div>
          <div className="flex flex-col">
            <span className="font-semibold text-gray-800">MFG Date</span>
            <span>{medicine.mfgDate}</span>
          </div>
          <div className="flex flex-col col-span-2">
            <span className="font-semibold text-gray-800">Created At</span>
            <span>{medicine.createdAt}</span>
          </div>
          <div className="flex flex-col col-span-2 mt-2">
            <span className="text-sm text-gray-500">Current Stock</span>
            <span className="text-lg font-bold text-gray-800">{medicine.quantity} units available</span>
          </div>
        </div>

        {/* Modal Actions */}
        <div className="flex justify-end gap-3 mt-6 border-t pt-4">
          <button className="px-6 py-2 text-sm font-semibold text-blue-600 border border-blue-600 rounded-lg hover:bg-blue-50 transition-colors">
            Purchase Return
          </button>
          <button className="px-6 py-2 text-sm font-semibold text-white bg-blue-600 rounded-lg shadow hover:bg-blue-700 transition-colors">
            Update
          </button>
        </div>
      </div>
    </div>,
    document.body
  );
};

const App = () => {
  const [medicines, setMedicines] = useState(MOCK_MEDICINES);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortField, setSortField] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');
  const [currentPage, setCurrentPage] = useState(1);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedMedicine, setSelectedMedicine] = useState(null);
  const itemsPerPage = 5;

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1); // Reset to first page on search
  };

  const handleSortChange = (e) => {
    const value = e.target.value;
    if (value === 'A-Z') {
      setSortField('name');
      setSortOrder('asc');
    } else if (value === 'Z-A') {
      setSortField('name');
      setSortOrder('desc');
    } else if (value === 'Price Low to High') {
      setSortField('price');
      setSortOrder('asc');
    } else if (value === 'Price High to Low') {
      setSortField('price');
      setSortOrder('desc');
    } else if (value === 'Quantity Low to High') {
      setSortField('quantity');
      setSortOrder('asc');
    } else if (value === 'Quantity High to Low') {
      setSortField('quantity');
      setSortOrder('desc');
    }
  };

  const openModal = (medicine) => {
    setSelectedMedicine(medicine);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setSelectedMedicine(null);
  };

  // Filter and sort the data
  const filteredAndSortedMedicines = useMemo(() => {
    let sortedList = [...medicines];

    // Sorting logic
    if (sortField) {
      sortedList.sort((a, b) => {
        const aValue = typeof a[sortField] === 'string' ? a[sortField].toLowerCase() : a[sortField];
        const bValue = typeof b[sortField] === 'string' ? b[sortField].toLowerCase() : b[sortField];

        if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
        if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
        return 0;
      });
    }

    // Filtering logic
    if (searchTerm) {
      const lowercasedSearchTerm = searchTerm.toLowerCase();
      sortedList = sortedList.filter((medicine) =>
        Object.values(medicine).some((value) =>
          String(value).toLowerCase().includes(lowercasedSearchTerm)
        )
      );
    }
    
    return sortedList;
  }, [medicines, searchTerm, sortField, sortOrder]);

  // Pagination logic
  const totalItems = filteredAndSortedMedicines.length;
  const totalPages = Math.ceil(totalItems / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedMedicines = filteredAndSortedMedicines.slice(
    startIndex,
    startIndex + itemsPerPage
  );

  return (
    <div className="font-['Inter'] min-h-screen bg-gray-100 p-4 sm:p-6 lg:p-8">
      <div className="max-w-7xl mx-auto bg-white rounded-2xl shadow-xl overflow-hidden">
        {/* Header */}
        <header className="flex items-center p-4 sm:p-6 border-b border-gray-200">
          <Menu />
          <h1 className="text-xl sm:text-2xl font-bold ml-4 text-gray-800">Stock Management</h1>
        </header>

        {/* Filters and Search Bar */}
        <div className="p-4 sm:p-6 flex flex-col md:flex-row items-stretch md:items-center gap-4 border-b border-gray-200">
          <div className="relative flex-1">
            <select
              className="w-full pl-4 pr-10 py-3 rounded-xl border border-gray-300 bg-white appearance-none focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm font-medium text-gray-700 shadow-sm"
              defaultValue="Medicine"
            >
              <option value="Medicine">Medicine</option>
              <option value="Brand">Brand</option>
              <option value="Batch">Batch</option>
              <option value="Expiry">Expiry</option>
            </select>
            <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
              <ChevronDown />
            </div>
          </div>

          <div className="relative flex-1 md:flex-3">
            <input
              type="text"
              placeholder="Search by medicine..."
              value={searchTerm}
              onChange={handleSearchChange}
              className="w-full pl-10 pr-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm shadow-sm"
            />
            <div className="absolute inset-y-0 left-0 flex items-center pl-3">
              <Search />
            </div>
          </div>

          <div className="relative flex-1">
            <select
              className="w-full pl-4 pr-10 py-3 rounded-xl border border-gray-300 bg-white appearance-none focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm font-medium text-gray-700 shadow-sm"
              onChange={handleSortChange}
              defaultValue="A-Z"
            >
              <option value="A-Z">A-Z</option>
              <option value="Z-A">Z-A</option>
              <option value="Price Low to High">Price Low to High</option>
              <option value="Price High to Low">Price High to Low</option>
              <option value="Quantity Low to High">Quantity Low to High</option>
              <option value="Quantity High to Low">Quantity High to Low</option>
            </select>
            <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
              <ChevronDown />
            </div>
          </div>
        </div>

        {/* Medicine Table */}
        <div className="p-4 sm:p-6 overflow-x-auto">
          <div className="min-w-full">
            <div className="grid grid-cols-[5%_25%_15%_15%_10%_15%_10%_5%] gap-4 py-3 px-4 text-sm font-semibold text-gray-500 uppercase bg-gray-50 rounded-t-xl sticky top-0 z-10">
              <div className="col-span-1">S.No</div>
              <div className="col-span-1">Medicine</div>
              <div className="col-span-1">Brand</div>
              <div className="col-span-1">Batch</div>
              <div className="col-span-1">Quantity</div>
              <div className="col-span-1">Expiry</div>
              <div className="col-span-1">Price</div>
              <div className="col-span-1 text-center">Action</div>
            </div>
            
            {paginatedMedicines.length > 0 ? (
              paginatedMedicines.map((medicine, index) => (
                <div
                  key={medicine.id}
                  className="grid grid-cols-[5%_25%_15%_15%_10%_15%_10%_5%] items-center gap-4 py-4 px-4 border-b last:border-b-0 border-gray-200 hover:bg-gray-50 transition-colors"
                >
                  <div className="col-span-1 text-sm font-medium text-gray-900">
                    {startIndex + index + 1}
                  </div>
                  <div className="col-span-1 text-sm text-gray-700 font-semibold">
                    {medicine.name}
                  </div>
                  <div className="col-span-1 text-sm text-gray-500">
                    {medicine.brand}
                  </div>
                  <div className="col-span-1 text-sm text-gray-500">
                    {medicine.batch}
                  </div>
                  <div className="col-span-1 text-xs text-center font-bold px-2 py-1 rounded-full w-fit mx-auto" style={{
                    backgroundColor: medicine.quantity < 100 ? '#fff3cd' : '#d4edda',
                    color: medicine.quantity < 100 ? '#856404' : '#155724'
                  }}>
                    {medicine.quantity}
                  </div>
                  <div className="col-span-1 text-sm text-gray-500">
                    {medicine.expiry}
                  </div>
                  <div className="col-span-1 text-sm font-semibold text-gray-800">
                    ₹{medicine.price.toFixed(2)}
                  </div>
                  <div className="col-span-1 text-center">
                    <button
                      onClick={() => openModal(medicine)}
                      className="text-white bg-blue-600 hover:bg-blue-700 transition-colors text-xs font-semibold px-4 py-2 rounded-lg shadow-md"
                    >
                      View
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8 text-gray-500">
                No medicines found.
              </div>
            )}
          </div>
        </div>

        {/* Pagination */}
        <div className="flex justify-between items-center p-4 sm:p-6 border-t border-gray-200">
          <button
            onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
            className="flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-600 bg-gray-100 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-200 transition-colors"
          >
            <ChevronDown />
            Prev
          </button>
          <div className="text-sm text-gray-500">
            Page {currentPage} of {totalPages} ({totalItems} total items)
          </div>
          <button
            onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
            className="flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-600 bg-gray-100 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-200 transition-colors"
          >
            Next
            <ChevronDown />
          </button>
        </div>
      </div>
      
      {/* The Modal */}
      <Modal show={modalOpen} onClose={closeModal} medicine={selectedMedicine} />
    </div>
  );
};

export default App;
