import React, { useEffect, useState } from "react";
import axios from "axios";
import AddWholesalerModal from "./AddWholesalerModal";
import BoxLoader from "./looader/BoxLoader";

const BASE_URL = "http://localhost:4000";
const token = localStorage.getItem("token") || "";

const Wholesaler = () => {
  const [wholesalers, setWholesalers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch wholesalers from API
  const fetchWholesalers = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${BASE_URL}/admin/wholesaler`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWholesalers(res.data || []);
    } catch (error) {
      console.error("Fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWholesalers();
  }, []);

  // Add wholesaler
  const handleAddWholesaler = async (wholesalerData) => {
    try {
      setLoading(true);
      await axios.post(`${BASE_URL}/admin/wholesaler`, wholesalerData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchWholesalers(); // refresh list
      setIsModalOpen(false);
    } catch (error) {
      console.error("Add error:", error);
    } finally {
      setLoading(false);
    }
  };

  // Delete wholesaler
  const handleDelete = async (id) => {
    try {
      setLoading(true);
      await axios.delete(`${BASE_URL}/admin/wholesaler/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchWholesalers();
    } catch (error) {
      console.error("Delete error:", error);
    } finally {
      setLoading(false);
    }
  };

  // Filtered list
  const filteredWholesalers = wholesalers.filter((item) =>
    item.wholesaler_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-4 relative">
      {loading && (
        <div className="absolute inset-0 z-40 flex items-center justify-center bg-white bg-opacity-50 backdrop-blur-sm">
          <BoxLoader />
        </div>
      )}

      <div className={isModalOpen ? "blur-sm pointer-events-none" : ""}>
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-semibold">Wholesalers</h1>
          <button
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            onClick={() => setIsModalOpen(true)}
          >
            + Add Wholesaler
          </button>
        </div>

        <input
          type="text"
          placeholder="Search by name..."
          className="w-full mb-4 px-3 py-2 border border-gray-300 rounded"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        <div className="overflow-x-auto">
          <table className="min-w-full bg-white shadow border rounded">
            <thead>
              <tr className="bg-gray-100">
                <th className="text-left px-4 py-2">Name</th>
                <th className="text-left px-4 py-2">Contact</th>
                <th className="text-left px-4 py-2">GST</th>
                <th className="text-left px-4 py-2">Email</th>
                <th className="text-left px-4 py-2">Address</th>
                <th className="text-left px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredWholesalers.length > 0 ? (
                filteredWholesalers.map((item) => (
                  <tr key={item.wholesaler_id} className="border-t">
                    <td className="px-4 py-2">{item.wholesaler_name}</td>
                    <td className="px-4 py-2">{item.contact}</td>
                    <td className="px-4 py-2">{item.gst}</td>
                    <td className="px-4 py-2">{item.email}</td>
                    <td className="px-4 py-2">{item.address}</td>
                    <td className="px-4 py-2">
                      <button
                        onClick={() => handleDelete(item.wholesaler_id)}
                        className="text-red-600 hover:underline"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td className="px-4 py-2 text-gray-500" colSpan="6">
                    No wholesalers found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {isModalOpen && (
        <AddWholesalerModal
          onClose={() => setIsModalOpen(false)}
          onSubmit={handleAddWholesaler}
        />
      )}
    </div>
  );
};

export default Wholesaler;
