import React, { useState } from "react";
import axios from "axios";

// Placeholder Modal Component
const AddMedicineModal = ({ onClose, onAddMedicine }) => {
  const [medicineName, setMedicineName] = useState('');
  const [brandName, setBrandName] = useState('');
  const [mfgDate, setMfgDate] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [packedType, setPackedType] = useState('');
  const [stockQuantity, setStockQuantity] = useState('');
  const [purchasePrice, setPurchasePrice] = useState('');
  const [mrp, setMrp] = useState('');
  const [batchNo, setBatchNo] = useState('');
  const [modalError, setModalError] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    setModalError(null);

    if (!medicineName || !brandName || !mfgDate || !expiryDate || !packedType || !stockQuantity || !purchasePrice || !mrp || !batchNo) {
      setModalError("Please fill all fields in the medicine form.");
      return;
    }

    onAddMedicine({
      medicine_name: medicineName,
      brand_name: brandName,
      mfg_date: mfgDate,
      expiry_date: expiryDate,
      packed_type: packedType,
      stock_quantity: parseInt(stockQuantity),
      purchase_price: parseFloat(purchasePrice),
      mrp: parseFloat(mrp),
      batch_no: batchNo,
    });

    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-lg w-full max-w-lg p-6">
        <h2 className="text-xl font-bold mb-4">Add New Medicine</h2>
        {modalError && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-2 rounded mb-4">
            {modalError}
          </div>
        )}
        <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4">
          <input className="input" placeholder="Medicine Name" value={medicineName} onChange={(e) => setMedicineName(e.target.value)} />
          <input className="input" placeholder="Brand Name" value={brandName} onChange={(e) => setBrandName(e.target.value)} />
          <input type="date" className="input" value={mfgDate} onChange={(e) => setMfgDate(e.target.value)} />
          <input type="date" className="input" value={expiryDate} onChange={(e) => setExpiryDate(e.target.value)} />
          <input className="input" placeholder="Packed Type" value={packedType} onChange={(e) => setPackedType(e.target.value)} />
          <input type="number" className="input" placeholder="Quantity" value={stockQuantity} onChange={(e) => setStockQuantity(e.target.value)} />
          <input type="number" step="0.01" className="input" placeholder="Purchase Price" value={purchasePrice} onChange={(e) => setPurchasePrice(e.target.value)} />
          <input type="number" step="0.01" className="input" placeholder="MRP" value={mrp} onChange={(e) => setMrp(e.target.value)} />
          <input className="input" placeholder="Batch No." value={batchNo} onChange={(e) => setBatchNo(e.target.value)} />
          <div className="flex justify-end space-x-2">
            <button type="button" onClick={onClose} className="bg-gray-300 text-gray-800 px-4 py-2 rounded">Cancel</button>
            <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Add Medicine</button>
          </div>
        </form>
      </div>
    </div>
  );
};

const BASE_URL = "http://localhost:4000";
const token = localStorage.getItem("token") || "";

const StockEntry = () => {
  const [wholesaler, setWholesaler] = useState("");
  const [invoiceNumber, setInvoiceNumber] = useState("");
  const [date, setDate] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleAddMedicine = (medicine) => setMedicines(prev => [...prev, medicine]);
  const handleDeleteMedicine = (idx) => setMedicines(prev => prev.filter((_, i) => i !== idx));

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setMessage(null);

    if (!wholesaler || !invoiceNumber || !date) {
      setError("Please fill all required fields.");
      return;
    }

    if (medicines.length === 0) {
      setError("Please add at least one medicine.");
      return;
    }

    setLoading(true);

    try {
      const res = await axios.post(`${BASE_URL}/admin/medicine_stock`, {
        wholesaler,
        invoiceNumber,
        date,
        medicine: medicines,
      }, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.status === 200) {
        setMessage(res.data.message || "Stock entry added successfully!");
        setWholesaler("");
        setInvoiceNumber("");
        setDate("");
        setMedicines([]);
      } else {
        setError(res.data.message || "Failed to add stock entry.");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Network error.");
    } finally {
      setLoading(false);
      setTimeout(() => {
        setMessage(null);
        setError(null);
      }, 3000);
    }
  };

  return (
    <div className="min-h-screen w-full bg-gray-100 p-4 sm:p-8 flex justify-center items-start">
      <form onSubmit={handleSubmit} className="w-full max-w-6xl bg-white rounded-lg shadow-lg p-6 sm:p-8">
        <h1 className="text-2xl font-semibold mb-6 text-theme-800">Add Stock Entry</h1>

        {/* Form Fields */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <input placeholder="Wholesaler *" className="input" value={wholesaler} onChange={(e) => setWholesaler(e.target.value)} />
          <input placeholder="Invoice Number *" className="input" value={invoiceNumber} onChange={(e) => setInvoiceNumber(e.target.value)} />
          <input type="date" className="input" value={date} onChange={(e) => setDate(e.target.value)} />
        </div>

        {/* Medicine Table */}
        <div className="mb-6">
          <div className="flex justify-between items-center mb-3">
            <h2 className="font-semibold text-lg">Medicines in this Invoice</h2>
            <button type="button" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700" onClick={() => setShowModal(true)}>+ Add Medicine</button>
          </div>

          <div className="overflow-x-auto border border-gray-200 rounded-lg">
            <table className="min-w-full text-sm">
              <thead className="bg-gray-100">
                <tr>
                  {["Medicine Name", "Brand", "MFG Date", "Expiry", "Pack", "Qty", "Purchase", "MRP", "Batch", "Action"].map((head, i) => (
                    <th key={i} className="px-3 py-2 text-left whitespace-nowrap min-w-[150px] font-semibold">{head}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {medicines.length === 0 ? (
                  <tr>
                    <td colSpan={10} className="text-center py-6 text-gray-400">No medicines added.</td>
                  </tr>
                ) : medicines.map((med, idx) => (
                  <tr key={idx} className="border-t">
                    <td className="px-3 py-2">{med.medicine_name}</td>
                    <td className="px-3 py-2">{med.brand_name}</td>
                    <td className="px-3 py-2">{med.mfg_date}</td>
                    <td className="px-3 py-2">{med.expiry_date}</td>
                    <td className="px-3 py-2">{med.packed_type}</td>
                    <td className="px-3 py-2">{med.stock_quantity}</td>
                    <td className="px-3 py-2">{med.purchase_price}</td>
                    <td className="px-3 py-2">{med.mrp}</td>
                    <td className="px-3 py-2">{med.batch_no}</td>
                    <td className="px-3 py-2">
                      <button type="button" onClick={() => handleDeleteMedicine(idx)} className="text-red-600 hover:underline">Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Alerts */}
        {error && <div className="bg-red-100 border border-red-400 text-red-700 p-3 rounded mb-4">{error}</div>}
        {message && <div className="bg-green-100 border border-green-400 text-green-700 p-3 rounded mb-4">{message}</div>}

        {/* Buttons */}
        <div className="flex justify-end gap-3">
          <button type="button" className="bg-gray-300 text-gray-800 px-5 py-2 rounded" onClick={() => {
            setWholesaler(""); setInvoiceNumber(""); setDate(""); setMedicines([]);
          }}>
            Cancel
          </button>
          <button type="submit" disabled={loading} className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">
            {loading ? "Submitting..." : "Submit Stock Entry"}
          </button>
        </div>
      </form>

      {showModal && (
        <AddMedicineModal
          onClose={() => setShowModal(false)}
          onAddMedicine={handleAddMedicine}
        />
      )}
    </div>
  );
};

export default StockEntry;
