import React, { useState, useEffect, useCallback, useRef } from 'react';

// Define your API base URL here
// IMPORTANT: Replace this with your actual backend API URL.
// For demonstration, it's set to a placeholder.
const BASE_URL = "http://your-api-base-url";

// AddMedicineModal Component
const AddMedicineModal = ({ onClose, onAddMedicine, BASE_URL }) => {
  const [medicineName, setMedicineName] = useState('');
  const [brand, setBrand] = useState('');
  const [mfgDate, setMfgDate] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [packedType, setPackedType] = useState('');
  const [quantity, setQuantity] = useState('');
  const [purchasePrice, setPurchasePrice] = useState('');
  const [mrp, setMrp] = useState('');
  const [batchNo, setBatchNo] = useState('');

  const [suggestions, setSuggestions] = useState([]);
  const [highlightedIndex, setHighlightedIndex] = useState(-1);
  const suggestionListRef = useRef(null);
  const medicineNameInputRef = useRef(null);

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'

  const debouncedSearch = useRef(null);

  // Clear debounce timeout on component unmount
  useEffect(() => {
    return () => {
      if (debouncedSearch.current) {
        clearTimeout(debouncedSearch.current);
      }
    };
  }, []);

  // Function to fetch medicine suggestions from the API
  const fetchSuggestions = useCallback(async (query) => {
    if (query.length < 2) { // Only fetch if query is at least 2 characters
      setSuggestions([]);
      return;
    }

    if (debouncedSearch.current) {
      clearTimeout(debouncedSearch.current);
    }

    // Set a timeout for debouncing the API call
    debouncedSearch.current = setTimeout(async () => {
      try {
        setLoading(true);
        const response = await fetch(`${BASE_URL}/admin/medicines/recommendation?query=${query}`);
        const data = await response.json();
        if (response.ok && data.status === 'success') {
          setSuggestions(data.recommendations || []);
          setHighlightedIndex(-1); // Reset highlight when new suggestions load
        } else {
          setSuggestions([]);
        }
      } catch (error) {
        console.error('Error fetching suggestions:', error);
        setSuggestions([]);
      } finally {
        setLoading(false);
      }
    }, 300); // Debounce delay of 300ms
  }, [BASE_URL]);

  // Function to fetch full medicine details by ID
  const fetchMedicineDetails = useCallback(async (medicineId) => {
    setLoading(true);
    setMessage(null);
    try {
      const response = await fetch(`${BASE_URL}/admin/medicine/${medicineId}`);
      const data = await response.json();
      if (response.ok && data.status === 'success' && data.medicine && data.medicine.length > 0) {
        const med = data.medicine[0];
        setMedicineName(med.medicine_name || '');
        setBrand(med.brand_name || '');
        // Format dates to YYYY-MM-DD for input type="date"
        setMfgDate(med.mfg_date ? new Date(med.mfg_date).toISOString().split('T')[0] : '');
        setExpiryDate(med.expiry_date ? new Date(med.expiry_date).toISOString().split('T')[0] : '');
        setPackedType(med.packed_type || '');
        setQuantity(med.stock_quantity || '');
        setPurchasePrice(med.purchase_price || '');
        setMrp(med.mrp || '');
        setBatchNo(med.batch_no || '');
        setSuggestions([]); // Clear suggestions after selection
      } else {
        setMessage('Failed to fetch medicine details.');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Error fetching medicine details:', error);
      setMessage('An error occurred while fetching medicine details.');
      setMessageType('error');
    } finally {
      setLoading(false);
      setTimeout(() => setMessage(null), 5000); // Clear message after 5 seconds
    }
  }, [BASE_URL]);

  // Handle changes in the medicine name input field
  const handleMedicineNameChange = (e) => {
    const value = e.target.value;
    setMedicineName(value);
    fetchSuggestions(value);
  };

  // Handle keyboard navigation for suggestions
  const handleKeyDown = (e) => {
    if (suggestions.length > 0) {
      if (e.key === 'ArrowDown') {
        e.preventDefault(); // Prevent cursor movement in input
        setHighlightedIndex((prevIndex) =>
          prevIndex < suggestions.length - 1 ? prevIndex + 1 : prevIndex
        );
      } else if (e.key === 'ArrowUp') {
        e.preventDefault(); // Prevent cursor movement in input
        setHighlightedIndex((prevIndex) =>
          prevIndex > 0 ? prevIndex - 1 : 0
        );
      } else if (e.key === 'Enter') {
        e.preventDefault(); // Prevent form submission
        if (highlightedIndex !== -1) {
          fetchMedicineDetails(suggestions[highlightedIndex].medicine_id);
        }
      }
    }
  };

  // Scroll to highlighted suggestion
  useEffect(() => {
    if (highlightedIndex !== -1 && suggestionListRef.current) {
      const highlightedItem = suggestionListRef.current.children[highlightedIndex];
      highlightedItem?.scrollIntoView({ block: 'nearest' });
    }
  }, [highlightedIndex]);

  // Handle adding a medicine to the parent component's state
  const handleAddMedicine = () => {
    // Basic validation for all fields
    if (!medicineName || !brand || !mfgDate || !expiryDate || !packedType || !quantity || !purchasePrice || !mrp || !batchNo) {
      setMessage('Please fill in all fields.');
      setMessageType('error');
      return;
    }

    const newMedicine = {
      packed_type: packedType,
      medicine_name: medicineName,
      brand_name: brand,
      batch_no: batchNo,
      purchase_price: parseFloat(purchasePrice).toFixed(2), // Format to 2 decimal places
      mrp: parseFloat(mrp).toFixed(2), // Format to 2 decimal places
      stock_quantity: parseInt(quantity, 10),
      mfg_date: mfgDate, // YYYY-MM-DD
      expiry_date: expiryDate, // YYYY-MM-DD
    };
    onAddMedicine(newMedicine); // Pass the new medicine object to parent
    onClose(); // Close modal after adding
    setMessage(null); // Clear any previous messages
  };

  // Handle refreshing (clearing) all modal fields
  const handleRefresh = () => {
    setMedicineName('');
    setBrand('');
    setMfgDate('');
    setExpiryDate('');
    setPackedType('');
    setQuantity('');
    setPurchasePrice('');
    setMrp('');
    setBatchNo('');
    setSuggestions([]);
    setHighlightedIndex(-1);
    setMessage(null);
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50 font-sans">
      <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-lg relative">
        {/* Modal Header */}
        <div className="flex justify-between items-center border-b pb-3 mb-4">
          <h2 className="text-2xl font-semibold text-gray-800">Add New Medicine</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700 text-2xl font-bold">
            &times;
          </button>
        </div>
        <p className="text-gray-600 mb-4">Enter details for the medicine to add to stock.</p>

        {/* Form Fields */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
          <div className="col-span-2 relative">
            <label htmlFor="medicineName" className="block text-sm font-medium text-gray-700 mb-1">Medicine Name</label>
            <input
              type="text"
              id="medicineName"
              ref={medicineNameInputRef}
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={medicineName}
              onChange={handleMedicineNameChange}
              onKeyDown={handleKeyDown}
              required
            />
            {loading && medicineName.length > 1 && (
              <div className="absolute top-1/2 -translate-y-1/2 right-3 z-10 flex items-center">
                <div className="loader ease-linear rounded-full border-2 border-t-2 border-gray-200 h-5 w-5"></div>
              </div>
            )}
            {suggestions.length > 0 && (
              <ul
                ref={suggestionListRef}
                className="absolute z-10 w-full bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-y-auto mt-1"
              >
                {suggestions.map((suggestion, index) => (
                  <li
                    key={suggestion.medicine_id}
                    className={`p-2 cursor-pointer hover:bg-blue-100 ${
                      index === highlightedIndex ? 'bg-blue-100' : ''
                    }`}
                    onClick={() => fetchMedicineDetails(suggestion.medicine_id)}
                  >
                    {suggestion.medicine_name}
                  </li>
                ))}
              </ul>
            )}
          </div>
          <div>
            <label htmlFor="brand" className="block text-sm font-medium text-gray-700 mb-1">Brand</label>
            <input
              type="text"
              id="brand"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={brand}
              onChange={(e) => setBrand(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="batchNo" className="block text-sm font-medium text-gray-700 mb-1">Batch No</label>
            <input
              type="text"
              id="batchNo"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={batchNo}
              onChange={(e) => setBatchNo(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="mfgDate" className="block text-sm font-medium text-gray-700 mb-1">MFG Date</label>
            <input
              type="date"
              id="mfgDate"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={mfgDate}
              onChange={(e) => setMfgDate(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="expiryDate" className="block text-sm font-medium text-gray-700 mb-1">Expiry Date</label>
            <input
              type="date"
              id="expiryDate"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={expiryDate}
              onChange={(e) => setExpiryDate(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="packedType" className="block text-sm font-medium text-gray-700 mb-1">Packed Type</label>
            <input
              type="text"
              id="packedType"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={packedType}
              onChange={(e) => setPackedType(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="quantity" className="block text-sm font-medium text-gray-700 mb-1">Quantity</label>
            <input
              type="number"
              id="quantity"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              required
              min="1"
            />
          </div>
          <div>
            <label htmlFor="purchasePrice" className="block text-sm font-medium text-gray-700 mb-1">Purchase Price (₹)</label>
            <input
              type="number"
              id="purchasePrice"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={purchasePrice}
              onChange={(e) => setPurchasePrice(e.target.value)}
              required
              step="0.01"
              min="0"
            />
          </div>
          <div>
            <label htmlFor="mrp" className="block text-sm font-medium text-gray-700 mb-1">MRP (₹)</label>
            <input
              type="number"
              id="mrp"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={mrp}
              onChange={(e) => setMrp(e.target.value)}
              required
              step="0.01"
              min="0"
            />
          </div>
        </div>

        {/* Message display for modal */}
        {message && (
          <div className={`mt-4 p-3 rounded-md text-sm ${messageType === 'success' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
            {message}
          </div>
        )}

        {/* Modal Buttons */}
        <div className="flex justify-end mt-6 space-x-4">
          <button
            onClick={onClose}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-lg shadow-md transition duration-300 ease-in-out"
          >
            Cancel
          </button>
          <button
            onClick={handleRefresh}
            className="bg-purple-500 hover:bg-purple-600 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300 ease-in-out"
          >
            Refresh
          </button>
          <button
            onClick={handleAddMedicine}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-6 rounded-lg shadow-md transition duration-300 ease-in-out"
            disabled={loading}
          >
            Add
          </button>
        </div>
      </div>
    </div>
  );
};

// Main App Component
function App() {
  const [wholesaler, setWholesaler] = useState('');
  const [invoiceNumber, setInvoiceNumber] = useState('');
  const [date, setDate] = useState('');
  const [medicines, setMedicines] = useState([]);
  const [showAddMedicineModal, setShowAddMedicineModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'

  // Function to add a new medicine to the invoice list
  const addMedicineToInvoice = useCallback((newMedicine) => {
    setMedicines((prevMedicines) => [...prevMedicines, newMedicine]);
    setShowAddMedicineModal(false); // Close modal after adding
  }, []);

  // Function to remove a medicine from the invoice list
  const removeMedicineFromInvoice = useCallback((indexToRemove) => {
    setMedicines((prevMedicines) => prevMedicines.filter((_, index) => index !== indexToRemove));
  }, []);

  // Function to handle the submission of the entire stock entry
  const handleSubmitStockEntry = useCallback(async () => {
    // Validate main form fields and ensure at least one medicine is added
    if (!wholesaler.trim() || !invoiceNumber.trim() || !date.trim() || medicines.length === 0) {
      setMessage('Please fill in all main form fields and add at least one medicine.');
      setMessageType('error');
      return;
    }

    setLoading(true);
    setMessage(null); // Clear previous messages

    // Prepare medicine data for the API request
    const formattedMedicines = medicines.map(med => ({
      packed_type: med.packed_type,
      medicine_name: med.medicine_name,
      brand_name: med.brand_name,
      batch_no: med.batch_no,
      purchase_price: med.purchase_price,
      mrp: med.mrp,
      stock_quantity: med.stock_quantity,
      mfg_date: med.mfg_date, // Already in YYYY-MM-DD format
      expiry_date: med.expiry_date, // Already in YYYY-MM-DD format
    }));

    try {
      const response = await fetch(`${BASE_URL}/admin/medicine`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          wholesaler,
          invoiceNumber,
          medicine: formattedMedicines,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message || 'Stock entry submitted successfully!');
        setMessageType('success');
        // Reset form after successful submission
        setWholesaler('');
        setInvoiceNumber('');
        setDate('');
        setMedicines([]);
      } else {
        setMessage(data.message || 'Failed to submit stock entry. Please try again.');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Error submitting stock entry:', error);
      setMessage('An error occurred while submitting stock entry. Please check your network connection.');
      setMessageType('error');
    } finally {
      setLoading(false);
      // Clear message after a few seconds
      setTimeout(() => setMessage(null), 5000);
    }
  }, [wholesaler, invoiceNumber, date, medicines]);

  return (
    <div className="min-h-screen bg-gray-100 p-4 font-sans">
      {/* Header */}
      <header className="flex items-center bg-white p-4 shadow-md rounded-lg mb-6">
        <span className="text-xl mr-4 text-gray-700 cursor-pointer">←</span>
        <h1 className="text-2xl font-semibold text-gray-800">Add New Stock Entry</h1>
      </header>

      {/* Main Form and Medicine Table */}
      <main className="bg-white p-6 rounded-lg shadow-md mb-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div>
            <label htmlFor="wholesaler" className="block text-sm font-medium text-gray-700 mb-1">Wholesaler</label>
            <input
              type="text"
              id="wholesaler"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={wholesaler}
              onChange={(e) => setWholesaler(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="invoiceNumber" className="block text-sm font-medium text-gray-700 mb-1">Invoice Number</label>
            <input
              type="text"
              id="invoiceNumber"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={invoiceNumber}
              onChange={(e) => setInvoiceNumber(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-1">Date</label>
            <input
              type="date"
              id="date"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>
        </div>

        {/* Medicines in this Invoice Section */}
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Medicines in this Invoice</h2>
        {medicines.length === 0 ? (
          <p className="text-gray-600 mb-4">No medicines added yet.</p>
        ) : (
          <div className="overflow-x-auto mb-6 rounded-lg border border-gray-200 shadow-sm">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Medicine Name</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Brand</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">MFG Date</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Expiry Date</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Packed Type</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Quantity</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Purchase Price</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">MRP</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Action</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {medicines.map((med, index) => (
                  <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.medicine_name}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.brand_name}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.mfg_date}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.expiry_date}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.packed_type}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.stock_quantity}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">₹{med.purchase_price}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">₹{med.mrp}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <button
                        onClick={() => removeMedicineFromInvoice(index)}
                        className="text-red-600 hover:text-red-900 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50 rounded-full p-1"
                        title="Delete Medicine"
                      >
                        <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                        </svg>
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Add Medicine Button */}
        <button
          onClick={() => setShowAddMedicineModal(true)}
          className="flex items-center bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-full shadow-md transition duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
        >
          <span className="text-xl mr-2">+</span> Add Medicine
        </button>

        {/* Loading and Message display */}
        {loading && (
          <div className="mt-4 flex items-center justify-center">
            <div className="loader ease-linear rounded-full border-4 border-t-4 border-gray-200 h-8 w-8"></div>
            <p className="ml-2 text-gray-600">Submitting...</p>
          </div>
        )}
        {message && (
          <div className={`mt-4 p-3 rounded-md ${messageType === 'success' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
            {message}
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex justify-end mt-6 space-x-4">
          <button
            onClick={() => {
              // Reset all form states
              setWholesaler('');
              setInvoiceNumber('');
              setDate('');
              setMedicines([]);
              setMessage(null);
              setMessageType('');
            }}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-lg shadow-md transition duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-opacity-50"
          >
            Cancel
          </button>
          <button
            onClick={handleSubmitStockEntry}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-6 rounded-lg shadow-md transition duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
            disabled={loading} // Disable button while loading
          >
            Submit Stock Entry
          </button>
        </div>
      </main>

      {/* Add Medicine Modal */}
      {showAddMedicineModal && (
        <AddMedicineModal
          onClose={() => setShowAddMedicineModal(false)}
          onAddMedicine={addMedicineToInvoice}
          BASE_URL={BASE_URL}
        />
      )}
    </div>
  );
}

export default App;
