import React, { useState } from "react";
import axios from "axios";

const AddMedicineModal = ({ onClose, onAddMedicine }) => {
  const [medicineName, setMedicineName] = useState('');
  const [brandName, setBrandName] = useState('');
  const [mfgDate, setMfgDate] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [packedType, setPackedType] = useState('');
  const [stockQuantity, setStockQuantity] = useState('');
  const [purchasePrice, setPurchasePrice] = useState('');
  const [mrp, setMrp] = useState('');
  const [batchNo, setBatchNo] = useState('');
  const [modalError, setModalError] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    setModalError(null);

    if (!medicineName || !brandName || !mfgDate || !expiryDate || !packedType || !stockQuantity || !purchasePrice || !mrp || !batchNo) {
      setModalError("Please fill all fields in the medicine form.");
      return;
    }

    onAddMedicine({
      medicine_name: medicineName,
      brand_name: brandName,
      mfg_date: mfgDate,
      expiry_date: expiryDate,
      packed_type: packedType,
      stock_quantity: parseInt(stockQuantity),
      purchase_price: parseFloat(purchasePrice),
      mrp: parseFloat(mrp),
      batch_no: batchNo,
    });
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50 overflow-y-auto">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-md sm:max-w-lg p-6 relative">
        <h2 className="text-lg sm:text-xl font-bold mb-4 text-gray-800">Add New Medicine</h2>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4">
          {modalError && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-md mb-2" role="alert">
              {modalError}
            </div>
          )}
          {[{
            label: "Medicine Name", value: medicineName, setter: setMedicineName, type: "text"
          }, {
            label: "Brand Name", value: brandName, setter: setBrandName, type: "text"
          }, {
            label: "MFG Date", value: mfgDate, setter: setMfgDate, type: "date"
          }, {
            label: "Expiry Date", value: expiryDate, setter: setExpiryDate, type: "date"
          }, {
            label: "Packed Type", value: packedType, setter: setPackedType, type: "text"
          }, {
            label: "Quantity", value: stockQuantity, setter: setStockQuantity, type: "number"
          }, {
            label: "Purchase Price", value: purchasePrice, setter: setPurchasePrice, type: "number"
          }, {
            label: "MRP", value: mrp, setter: setMrp, type: "number"
          }, {
            label: "Batch No.", value: batchNo, setter: setBatchNo, type: "text"
          }].map(({ label, value, setter, type }, idx) => (
            <div key={idx}>
              <label className="block text-sm sm:text-base font-medium text-gray-700">{label}</label>
              <input type={type} value={value} onChange={(e) => setter(e.target.value)} required
                className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 text-sm sm:text-base focus:ring-blue-500 focus:border-blue-500" />
            </div>
          ))}
          <div className="flex flex-col sm:flex-row justify-end gap-2 mt-4">
            <button type="button" onClick={onClose} className="w-full sm:w-auto px-4 py-2 bg-gray-200 text-gray-800 rounded-md hover:bg-gray-300 text-sm sm:text-base">Cancel</button>
            <button type="submit" className="w-full sm:w-auto px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 text-sm sm:text-base">Add Medicine</button>
          </div>
        </form>
      </div>
    </div>
  );
};

const BASE_URL = "http://localhost:4000";
const token = localStorage.getItem('token') || "";

function StockEntry() {
  const [wholesaler, setWholesaler] = useState("");
  const [invoiceNumber, setInvoiceNumber] = useState("");
  const [date, setDate] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleAddMedicine = (medicine) => setMedicines(prev => [...prev, medicine]);
  const handleDeleteMedicine = (idx) => setMedicines(prev => prev.filter((_, i) => i !== idx));

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setMessage(null);

    if (!wholesaler || !invoiceNumber || !date) return setError("Please fill all required fields.");
    if (medicines.length === 0) return setError("Please add at least one medicine.");

    setLoading(true);
    try {
      const res = await axios.post(`${BASE_URL}/admin/medicine_stock`, {
        wholesaler,
        invoiceNumber,
        date,
        medicine: medicines
      }, {
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        }
      });

      const data = res.data;
      if (res.status === 200) {
        setMessage(data.message || "Stock entry added successfully!");
        setWholesaler("");
        setInvoiceNumber("");
        setDate("");
        setMedicines([]);
      } else {
        setError(data.message || "Failed to add stock entry. Please try again.");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Network error or failed to connect to the server.");
    } finally {
      setLoading(false);
      setTimeout(() => { setMessage(null); setError(null); }, 3000);
    }
  };

  return (
    <div className="w-full flex flex-col font-sans bg-gray-100 flex-1 px-4 sm:px-6">
      <form onSubmit={handleSubmit} className="w-full max-w-4xl mx-auto bg-white rounded-lg shadow-lg px-4 sm:px-6 py-6 mt-6 sm:mt-8 mb-8">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mb-6">
          {[{
            id: "wholesaler", label: "Wholesaler", value: wholesaler, setter: setWholesaler
          }, {
            id: "invoiceNumber", label: "Invoice Number", value: invoiceNumber, setter: setInvoiceNumber
          }, {
            id: "date", label: "Date", value: date, setter: setDate, type: "date"
          }].map(({ id, label, value, setter, type = "text" }, i) => (
            <div key={i}>
              <label htmlFor={id} className="block font-medium mb-1 text-sm sm:text-base text-gray-700">{label} *</label>
              <input id={id} type={type} value={value} onChange={(e) => setter(e.target.value)}
                className="w-full border border-gray-300 rounded-md px-3 py-2 text-sm sm:text-base focus:ring-blue-500 focus:border-blue-500" required />
            </div>
          ))}
        </div>

        <div className="mb-4">
          <div className="flex justify-between items-center mb-4 flex-wrap gap-3">
            <h2 className="font-semibold text-base sm:text-lg text-gray-800">Medicines in this Invoice</h2>
            <button type="button" onClick={() => setShowModal(true)}
              className="flex items-center bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors shadow-sm text-sm sm:text-base">
              <span className="text-xl mr-2 leading-none">+</span> Add Medicine
            </button>
          </div>
          <div className="w-full overflow-x-auto">
            <div className="min-w-[1000px] overflow-y-auto max-h-96 rounded-lg border border-gray-200 shadow-sm">
              <table className="w-full text-xs sm:text-sm divide-y divide-gray-200">
                <thead className="bg-gray-100 text-gray-800 sticky top-0 z-10">
                  <tr>
                    {["Medicine Name", "Brand", "MFG Date", "Expiry Date", "Packed Type", "Qty", "Purchase", "MRP", "Batch", "Action"].map((col, i) => (
                      <th key={i} className="px-3 py-3 text-left font-semibold whitespace-nowrap">{col}</th>
                    ))}
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-100">
                  {medicines.length === 0 ? (
                    <tr>
                      <td colSpan={10} className="text-center py-6 text-gray-400 italic">No medicines added yet.</td>
                    </tr>
                  ) : medicines.map((med, idx) => (
                    <tr key={idx} className={idx % 2 === 0 ? "bg-white" : "bg-gray-50"}>
                      {[med.medicine_name, med.brand_name, med.mfg_date, med.expiry_date, med.packed_type, med.stock_quantity, med.purchase_price, med.mrp, med.batch_no].map((val, i) => (
                        <td key={i} className="px-3 py-2 whitespace-nowrap">{val}</td>
                      ))}
                      <td className="px-3 py-2">
                        <button type="button" onClick={() => handleDeleteMedicine(idx)}
                          className="text-red-500 hover:text-red-700 p-1 rounded-full hover:bg-red-100 transition-colors">🗑️</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-md mb-4">{error}</div>}
        {message && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded-md mb-4">{message}</div>}

        <div className="flex flex-col sm:flex-row justify-end items-center gap-3 mt-6">
          <button type="button" onClick={() => {
            setWholesaler("");
            setInvoiceNumber("");
            setDate("");
            setMedicines([]);
            setError(null);
            setMessage(null);
          }}
            className="w-full sm:w-auto px-6 py-2 rounded-md bg-gray-200 text-gray-800 hover:bg-gray-300 transition-colors font-medium shadow-sm">
            Cancel
          </button>
          <button type="submit" disabled={loading}
            className="w-full sm:w-auto px-6 py-2 rounded-md bg-blue-600 text-white hover:bg-blue-700 transition-colors font-medium shadow-md disabled:opacity-50">
            {loading ? "Submitting..." : "Submit Stock Entry"}
          </button>
        </div>
      </form>

      {showModal && <AddMedicineModal onClose={() => setShowModal(false)} onAddMedicine={handleAddMedicine} />}
    </div>
  );
}

export default StockEntry;
