import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, ChevronDown, ChevronLeft, ChevronRight, Menu, X, Filter } from 'lucide-react';
// import { BASE_URL } from './config'; // Assuming you have a config file for your base URL

// Helper function to format dates
const formatDate = (dateString) => {
  if (!dateString) return 'N/A';
  const date = new Date(dateString);
  return date.toLocaleDateString('en-GB', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
  });
};

// Helper function to sort data
const sortData = (data, sortOption) => {
  switch (sortOption) {
    case 'A-Z':
      return [...data].sort((a, b) => a.medicine_name.localeCompare(b.medicine_name));
    case 'Z-A':
      return [...data].sort((a, b) => b.medicine_name.localeCompare(a.medicine_name));
    case 'Price High to Low':
      return [...data].sort((a, b) => parseFloat(b.mrp) - parseFloat(a.mrp));
    case 'Price Low to High':
      return [...data].sort((a, b) => parseFloat(a.mrp) - parseFloat(b.mrp));
    case 'Quantity High to Low':
      return [...data].sort((a, b) => b.quantity - a.quantity);
    case 'Quantity Low to High':
      return [...data].sort((a, b) => a.quantity - b.quantity);
    default:
      return data;
  }
};

// Custom Dropdown component to replace shadcn
const SortDropdown = ({ sortOption, setSortOption }) => {
  const [isOpen, setIsOpen] = useState(false);
  const options = ['A-Z', 'Z-A', 'Price Low to High', 'Price High to Low', 'Quantity Low to High', 'Quantity High to Low'];

  const handleSelect = (option) => {
    setSortOption(option);
    setIsOpen(false);
  };

  return (
    <div className="relative">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="flex items-center space-x-2 rounded-full border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 transition-colors hover:bg-gray-50"
      >
        <span className="hidden md:inline">{sortOption}</span>
        <ChevronDown className="h-4 w-4" />
      </button>
      {isOpen && (
        <div className="absolute right-0 z-10 mt-2 w-56 origin-top-right rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none">
          <div className="py-1">
            <div className="px-4 py-2 text-xs font-semibold text-gray-900">Sort by</div>
            <hr className="my-1 border-gray-100" />
            {options.map((option) => (
              <button
                key={option}
                onClick={() => handleSelect(option)}
                className="block w-full px-4 py-2 text-left text-sm text-gray-700 hover:bg-gray-100"
              >
                {option}
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};


const MedicineModal = ({ isOpen, onClose, medicine, onUpdate, onDelete }) => {
  const [formData, setFormData] = useState(medicine);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (medicine) {
      setFormData(medicine);
      setMessage('');
    }
  }, [medicine]);

  if (!isOpen || !medicine) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleUpdate = async () => {
    setLoading(true);
    setMessage('');
    try {
      const response = await axios.put(`${BASE_URL}/admin/medicine_stock/${medicine.id}`, formData);
      setMessage('Update successful!');
      onUpdate(response.data); // Pass the updated data back to the parent component
    } catch (error) {
      setMessage('Update failed. Please try again.');
      console.error('Update failed:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    const medicineName = window.prompt(`To confirm deletion, please type the medicine name exactly as it appears: "${medicine.medicine_name}"`);
    if (medicineName === medicine.medicine_name) {
      setLoading(true);
      setMessage('');
      try {
        await axios.delete(`${BASE_URL}/admin/medicine_stock/${medicine.id}`);
        setMessage('Deletion successful!');
        onDelete(medicine.id); // Pass the deleted item's ID back
        setTimeout(onClose, 2000); // Close the modal after a delay
      } catch (error) {
        setMessage('Deletion failed. Please try again.');
        console.error('Deletion failed:', error);
      } finally {
        setLoading(false);
      }
    } else if (medicineName !== null) {
      setMessage('Medicine name did not match. Deletion cancelled.');
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-gray-800 bg-opacity-50">
      <div className="w-full max-w-lg rounded-xl bg-white p-6 shadow-2xl">
        <div className="flex items-center justify-between border-b pb-4">
          <h2 className="text-xl font-bold">Update Medicine Stock</h2>
          <button onClick={onClose} className="rounded-full p-2 hover:bg-gray-100">
            <X size={20} className="text-gray-600" />
          </button>
        </div>
        <form className="mt-6 grid grid-cols-2 gap-4 text-sm text-gray-700">
          {Object.keys(formData).map((key) => {
            // Exclude non-editable fields
            if (['invoice_no', 'wholesaler', 'id', 'created_at'].includes(key)) {
              return (
                <div key={key}>
                  <label className="font-semibold">{key.replace(/_/g, ' ')}</label>
                  <input
                    type="text"
                    name={key}
                    value={formData[key]}
                    className="w-full rounded-md border border-gray-300 p-2 text-gray-500 bg-gray-100 cursor-not-allowed"
                    disabled
                  />
                </div>
              );
            }
            return (
              <div key={key}>
                <label className="font-semibold">{key.replace(/_/g, ' ')}</label>
                <input
                  type="text"
                  name={key}
                  value={formData[key] || ''}
                  onChange={handleChange}
                  className="w-full rounded-md border border-gray-300 p-2 focus:border-blue-500 focus:outline-none"
                />
              </div>
            );
          })}
        </form>
        {loading && (
          <div className="mt-4 text-center text-blue-600">
            <p>Processing...</p>
          </div>
        )}
        {message && (
          <div className={`mt-4 text-center font-semibold ${message.includes('successful') ? 'text-green-600' : 'text-red-600'}`}>
            <p>{message}</p>
          </div>
        )}
        <div className="mt-6 flex justify-end space-x-2">
          <button
            onClick={handleDelete}
            className="rounded-md bg-red-600 px-4 py-2 text-sm font-semibold text-white transition-colors hover:bg-red-700 disabled:opacity-50"
            disabled={loading}
          >
            Delete
          </button>
          <button
            onClick={handleUpdate}
            className="rounded-md bg-blue-600 px-4 py-2 text-sm font-semibold text-white transition-colors hover:bg-blue-700 disabled:opacity-50"
            disabled={loading}
          >
            Update
          </button>
        </div>
      </div>
    </div>
  );
};

export default function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('A-Z');
  const [viewMode, setViewMode] = useState('table');
  const [selectedMedicine, setSelectedMedicine] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [medicineData, setMedicineData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchMedicineData = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${BASE_URL}/admin/medicine_stock`);
      setMedicineData(response.data.rows);
      setError(null);
    } catch (err) {
      setError('Failed to fetch data. Please try again.');
      console.error('API fetch error:', err);
      setMedicineData([]);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchMedicineData();
  }, []);

  const handleUpdate = (updatedMedicine) => {
    setMedicineData((prevData) =>
      prevData.map((med) => (med.id === updatedMedicine.id ? updatedMedicine : med))
    );
    setSelectedMedicine(updatedMedicine);
    setIsModalOpen(false); // Close modal on successful update
  };

  const handleDelete = (deletedId) => {
    setMedicineData((prevData) => prevData.filter((med) => med.id !== deletedId));
    setIsModalOpen(false); // Close modal on successful delete
  };

  const filteredData = medicineData.filter(medicine =>
    medicine.medicine_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const sortedData = sortData(filteredData, sortOption);
  const itemsPerPage = 4;
  const totalItems = sortedData.length;
  const totalPages = Math.ceil(totalItems / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedData = sortedData.slice(startIndex, startIndex + itemsPerPage);

  const handlePageChange = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  const handleViewDetails = (medicine) => {
    setSelectedMedicine(medicine);
    setIsModalOpen(true);
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans text-gray-800 antialiased">
      <div className="container mx-auto px-4 py-8 md:px-6 lg:px-8">
        {/* Header Section */}
        <header className="flex items-center space-x-2 rounded-xl bg-white p-4 shadow-sm md:p-6">
          <div className="ml-auto flex flex-grow items-center justify-end space-x-4">
            <div className="relative flex-grow max-w-sm">
              <input
                type="text"
                placeholder="Search by medicine..."
                className="w-full rounded-full border border-gray-300 py-2 pl-10 pr-4 text-sm focus:border-blue-500 focus:outline-none"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 transform text-gray-400" size={18} />
            </div>
            <button
              onClick={() => setViewMode(viewMode === 'card' ? 'table' : 'card')}
              className="rounded-full bg-gray-100 p-2 text-gray-500 transition-colors hover:bg-gray-200"
              title={viewMode === 'card' ? 'Switch to Table View' : 'Switch to Card View'}
            >
              <Filter className="h-5 w-5" />
            </button>
            <SortDropdown sortOption={sortOption} setSortOption={setSortOption} />
          </div>
        </header>

        {/* Main Content Area */}
        <main className="mt-8">
          <h2 className="mb-4 text-center text-2xl font-bold text-blue-600">Medicine List</h2>
          {isLoading && (
            <div className="text-center text-lg text-gray-500">Loading medicine data...</div>
          )}
          {error && (
            <div className="text-center text-lg text-red-500">{error}</div>
          )}
          {!isLoading && !error && (
            <>
              {viewMode === 'table' ? (
                <div className="overflow-x-auto rounded-xl border border-gray-200 bg-white shadow-sm">
                  <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50 text-xs uppercase text-gray-500">
                      <tr>
                        <th scope="col" className="px-4 py-3 text-left font-medium">S.No</th>
                        <th scope="col" className="px-4 py-3 text-left font-medium">Medicine</th>
                        <th scope="col" className="px-4 py-3 text-left font-medium">Brand</th>
                        <th scope="col" className="px-4 py-3 text-left font-medium">Batch</th>
                        <th scope="col" className="px-4 py-3 text-left font-medium">Quantity</th>
                        <th scope="col" className="px-4 py-3 text-left font-medium">Expiry</th>
                        <th scope="col" className="px-4 py-3 text-left font-medium">MRP</th>
                        <th scope="col" className="px-4 py-3 text-center font-medium">Action</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200 bg-white">
                      {paginatedData.length > 0 ? (
                        paginatedData.map((medicine, index) => (
                          <tr key={medicine.id} className="text-sm">
                            <td className="whitespace-nowrap px-4 py-4 font-medium text-gray-900">{startIndex + index + 1}</td>
                            <td className="whitespace-nowrap px-4 py-4 text-gray-700">{medicine.medicine_name}</td>
                            <td className="whitespace-nowrap px-4 py-4 text-gray-700">{medicine.brand}</td>
                            <td className="whitespace-nowrap px-4 py-4 text-gray-700">{medicine.batch_no}</td>
                            <td className="whitespace-nowrap px-4 py-4 text-center">
                              <span className={`inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium ${medicine.quantity < 100 ? 'bg-yellow-100 text-yellow-800' : 'bg-green-100 text-green-800'}`}>
                                {medicine.quantity}
                              </span>
                            </td>
                            <td className="whitespace-nowrap px-4 py-4 text-gray-700">{formatDate(medicine.expiry_date)}</td>
                            <td className="whitespace-nowrap px-4 py-4 font-semibold text-gray-900">{parseFloat(medicine.mrp).toFixed(2)}</td>
                            <td className="whitespace-nowrap px-4 py-4 text-center">
                              <button
                                onClick={() => handleViewDetails(medicine)}
                                className="rounded-md bg-blue-600 px-4 py-2 text-sm font-semibold text-white shadow-sm transition-colors hover:bg-blue-700"
                              >
                                View
                              </button>
                            </td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="8" className="px-4 py-4 text-center text-gray-500">No medicines found.</td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>
              ) : (
                <div className="space-y-4">
                  {paginatedData.length > 0 ? (
                    paginatedData.map((medicine) => (
                      <div key={medicine.id} className="flex items-center rounded-xl bg-white p-4 shadow-sm">
                        <div className="flex-grow">
                          <h3 className="text-lg font-semibold text-gray-900">{medicine.medicine_name}</h3>
                          <p className="text-sm text-gray-500">{medicine.brand}</p>
                          <div className="mt-2 flex items-center space-x-4 text-sm text-gray-600">
                            <p>
                              <span className="font-medium">Batch:</span> {medicine.batch_no}
                            </p>
                            <p>
                              <span className="font-medium">Quantity:</span>{' '}
                              <span className={`inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium ${medicine.quantity < 100 ? 'bg-yellow-100 text-yellow-800' : 'bg-green-100 text-green-800'}`}>
                                {medicine.quantity}
                              </span>
                            </p>
                          </div>
                        </div>
                        <div className="ml-auto text-right">
                          <p className="text-lg font-bold text-gray-900">{parseFloat(medicine.mrp).toFixed(2)}</p>
                          <p className="text-sm text-gray-500">
                            Expiry: {formatDate(medicine.expiry_date)}
                          </p>
                          <button
                            onClick={() => handleViewDetails(medicine)}
                            className="mt-2 rounded-md bg-blue-600 px-4 py-2 text-sm font-semibold text-white shadow-sm transition-colors hover:bg-blue-700"
                          >
                            View
                          </button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p className="text-center text-gray-500">No medicines found.</p>
                  )}
                </div>
              )}
            </>
          )}
        </main>

        {/* Pagination */}
        <div className="mt-8 flex items-center justify-between border-t border-gray-200 bg-white px-4 py-4 sm:px-6">
          <div className="flex flex-1 justify-between sm:hidden">
            <button
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 1}
              className="relative inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50"
            >
              <ChevronLeft className="h-4 w-4 mr-2" /> Prev
            </button>
            <button
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages}
              className="relative ml-3 inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50"
            >
              Next <ChevronRight className="h-4 w-4 ml-2" />
            </button>
          </div>
          <div className="hidden sm:flex sm:flex-1 sm:items-center sm:justify-between">
            <div>
              <p className="text-sm text-gray-700">
                Page <span className="font-medium">{currentPage}</span> of <span className="font-medium">{totalPages}</span> ({totalItems} total items)
              </p>
            </div>
            <div>
              <nav className="isolate inline-flex -space-x-px rounded-md shadow-sm" aria-label="Pagination">
                <button
                  onClick={() => handlePageChange(currentPage - 1)}
                  disabled={currentPage === 1}
                  className="relative inline-flex items-center rounded-l-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0 disabled:opacity-50"
                >
                  <span className="sr-only">Previous</span>
                  <ChevronLeft className="h-5 w-5" aria-hidden="true" />
                </button>
                <button
                  onClick={() => handlePageChange(currentPage + 1)}
                  disabled={currentPage === totalPages}
                  className="relative inline-flex items-center rounded-r-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0 disabled:opacity-50"
                >
                  <span className="sr-only">Next</span>
                  <ChevronRight className="h-5 w-5" aria-hidden="true" />
                </button>
              </nav>
            </div>
          </div>
        </div>
      </div>
      <MedicineModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        medicine={selectedMedicine}
        onUpdate={handleUpdate}
        onDelete={handleDelete}
      />
    </div>
  );
}
