import React, { useState } from "react";
import axios from "axios";
import AddMedicineModal from "./AddMedicineModal";

const BASE_URL = "http://localhost:4000";
const token = localStorage.getItem('token') || "";

function StockEntry() {
  const [wholesalerNo, setWholesalerNo] = useState("");
  const [invoiceNo, setInvoiceNo] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);

  const handleAddMedicine = (medicine) => {
    setMedicines([...medicines, medicine]);
    setShowAddModal(false);
  };

  const handleDeleteMedicine = (index) => {
    const updatedList = [...medicines];
    updatedList.splice(index, 1);
    setMedicines(updatedList);
  };

  const handleSubmit = async () => {
    if (!wholesalerNo || !invoiceNo || medicines.length === 0) {
      alert("Please fill in all fields and add at least one medicine.");
      return;
    }

    try {
      const res = await axios.post(
        `${BASE_URL}/admin/stock/add`,
        { wholesalerNo, invoiceNo, medicines },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert("Stock added successfully!");
      setWholesalerNo("");
      setInvoiceNo("");
      setMedicines([]);
    } catch (error) {
      console.error("Error adding stock:", error);
      alert("Something went wrong!");
    }
  };

  return (
    <div className="p-4 md:p-8 max-w-screen-xl mx-auto">
      <h2 className="text-xl md:text-2xl font-bold mb-6 text-theme-700">Add Stock Entry</h2>

      {/* Input Fields */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <div>
          <label className="block font-medium text-theme-600 mb-1">Wholesaler No</label>
          <input
            type="text"
            value={wholesalerNo}
            onChange={(e) => setWholesalerNo(e.target.value)}
            className="w-full border border-gray-300 px-3 py-2 rounded-md"
            placeholder="Enter Wholesaler No"
          />
        </div>
        <div>
          <label className="block font-medium text-theme-600 mb-1">Invoice No</label>
          <input
            type="text"
            value={invoiceNo}
            onChange={(e) => setInvoiceNo(e.target.value)}
            className="w-full border border-gray-300 px-3 py-2 rounded-md"
            placeholder="Enter Invoice No"
          />
        </div>
      </div>

      {/* Add Medicine Button */}
      <div className="mb-4">
        <button
          onClick={() => setShowAddModal(true)}
          className="bg-theme-600 text-white px-4 py-2 rounded hover:bg-theme-700 transition"
        >
          + Add Medicine
        </button>
      </div>

      {/* Medicine Table */}
      <div className="overflow-x-auto">
        <table className="min-w-[900px] w-full text-sm border border-gray-200 rounded-lg overflow-hidden">
          <thead className="bg-theme-100 text-theme-800">
            <tr>
              {[
                "Medicine Name",
                "Brand",
                "MFG Date",
                "Expiry",
                "Packed Type",
                "Qty",
                "Purchase ₹",
                "MRP ₹",
                "Batch",
                "Action"
              ].map((head, idx) => (
                <th key={idx} className="px-3 py-2 text-left whitespace-nowrap font-medium">
                  {head}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {medicines.length === 0 ? (
              <tr>
                <td colSpan={10} className="text-center py-4 text-gray-400 italic">
                  No medicines added.
                </td>
              </tr>
            ) : (
              medicines.map((med, idx) => (
                <tr key={idx} className="even:bg-gray-50">
                  <td className="px-3 py-2">{med.medicine_name}</td>
                  <td className="px-3 py-2">{med.brand_name}</td>
                  <td className="px-3 py-2">{med.mfg_date}</td>
                  <td className="px-3 py-2">{med.expiry_date}</td>
                  <td className="px-3 py-2">{med.packed_type}</td>
                  <td className="px-3 py-2">{med.stock_quantity}</td>
                  <td className="px-3 py-2">{med.purchase_price}</td>
                  <td className="px-3 py-2">{med.mrp}</td>
                  <td className="px-3 py-2">{med.batch_no}</td>
                  <td className="px-3 py-2">
                    <button
                      className="text-red-500 hover:text-red-700"
                      onClick={() => handleDeleteMedicine(idx)}
                    >
                      🗑️
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Submit Button */}
      <div className="mt-6 flex justify-end">
        <button
          onClick={handleSubmit}
          className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 transition"
        >
          Add Stock
        </button>
      </div>

      {/* Modal */}
      {showAddModal && (
        <AddMedicineModal
          onClose={() => setShowAddModal(false)}
          onAdd={handleAddMedicine}
        />
      )}
    </div>
  );
}

export default StockEntry;
