// App.js
import React, { useState, useEffect, useCallback } from 'react';
import AddMedicineModal from './AddMedicineModal';

const BASE_URL = "http://your-api-base-url"; // Define your API base URL here

function App() {
  const [wholesaler, setWholesaler] = useState('');
  const [invoiceNumber, setInvoiceNumber] = useState('');
  const [date, setDate] = useState('');
  const [medicines, setMedicines] = useState([]);
  const [showAddMedicineModal, setShowAddMedicineModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'

  // Function to add a new medicine to the invoice
  const addMedicineToInvoice = useCallback((newMedicine) => {
    setMedicines((prevMedicines) => [...prevMedicines, newMedicine]);
    setShowAddMedicineModal(false);
  }, []);

  // Function to remove a medicine from the invoice
  const removeMedicineFromInvoice = useCallback((indexToRemove) => {
    setMedicines((prevMedicines) => prevMedicines.filter((_, index) => index !== indexToRemove));
  }, []);

  // Function to handle form submission
  const handleSubmitStockEntry = useCallback(async () => {
    if (!wholesaler || !invoiceNumber || !date || medicines.length === 0) {
      setMessage('Please fill in all main form fields and add at least one medicine.');
      setMessageType('error');
      return;
    }

    setLoading(true);
    setMessage(null);

    const formattedMedicines = medicines.map(med => ({
      ...med,
      mfg_date: med.mfg_date, // Already YYYY-MM-DD from modal
      expiry_date: med.expiry_date, // Already YYYY-MM-DD from modal
    }));

    try {
      const response = await fetch(`${BASE_URL}/admin/medicine`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          wholesaler,
          invoiceNumber,
          medicine: formattedMedicines,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message || 'Stock entry submitted successfully!');
        setMessageType('success');
        // Reset form after successful submission
        setWholesaler('');
        setInvoiceNumber('');
        setDate('');
        setMedicines([]);
      } else {
        setMessage(data.message || 'Failed to submit stock entry.');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Error submitting stock entry:', error);
      setMessage('An error occurred while submitting stock entry.');
      setMessageType('error');
    } finally {
      setLoading(false);
      // Clear message after a few seconds
      setTimeout(() => setMessage(null), 5000);
    }
  }, [wholesaler, invoiceNumber, date, medicines]);

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      {/* Header */}
      <header className="flex items-center bg-white p-4 shadow-md rounded-lg mb-6">
        <span className="text-xl mr-4 cursor-pointer">‚Üê</span>
        <h1 className="text-2xl font-semibold">Add New Stock Entry</h1>
      </header>

      {/* Main Form */}
      <main className="bg-white p-6 rounded-lg shadow-md mb-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div>
            <label htmlFor="wholesaler" className="block text-sm font-medium text-gray-700 mb-1">Wholesaler</label>
            <input
              type="text"
              id="wholesaler"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={wholesaler}
              onChange={(e) => setWholesaler(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="invoiceNumber" className="block text-sm font-medium text-gray-700 mb-1">Invoice Number</label>
            <input
              type="text"
              id="invoiceNumber"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={invoiceNumber}
              onChange={(e) => setInvoiceNumber(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-1">Date</label>
            <input
              type="date"
              id="date"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>
        </div>

        {/* Medicines in this Invoice Section */}
        <h2 className="text-xl font-semibold mb-4">Medicines in this Invoice</h2>
        {medicines.length === 0 ? (
          <p className="text-gray-600 mb-4">No medicines added yet.</p>
        ) : (
          <div className="overflow-x-auto mb-6">
            <table className="min-w-full divide-y divide-gray-200 border border-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Medicine Name</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Brand</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">MFG Date</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Expiry Date</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Packed Type</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Quantity</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Purchase Price</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">MRP</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Action</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {medicines.map((med, index) => (
                  <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.medicine_name}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.brand_name}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.mfg_date}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.expiry_date}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.packed_type}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.stock_quantity}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.purchase_price}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.mrp}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <button
                        onClick={() => removeMedicineFromInvoice(index)}
                        className="text-red-600 hover:text-red-900"
                        title="Delete Medicine"
                      >
                        <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                        </svg>
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Add Medicine Button */}
        <button
          onClick={() => setShowAddMedicineModal(true)}
          className="flex items-center bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-full shadow-md transition duration-300 ease-in-out"
        >
          <span className="text-xl mr-2">+</span> Add Medicine
        </button>

        {/* Loading and Message display */}
        {loading && (
          <div className="mt-4 flex items-center justify-center">
            <div className="loader ease-linear rounded-full border-4 border-t-4 border-gray-200 h-8 w-8"></div>
            <p className="ml-2 text-gray-600">Submitting...</p>
          </div>
        )}
        {message && (
          <div className={`mt-4 p-3 rounded-md ${messageType === 'success' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
            {message}
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex justify-end mt-6 space-x-4">
          <button
            onClick={() => {
              setWholesaler('');
              setInvoiceNumber('');
              setDate('');
              setMedicines([]);
              setMessage(null);
            }}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-lg shadow-md transition duration-300 ease-in-out"
          >
            Cancel
          </button>
          <button
            onClick={handleSubmitStockEntry}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-6 rounded-lg shadow-md transition duration-300 ease-in-out"
            disabled={loading}
          >
            Submit Stock Entry
          </button>
        </div>
      </main>

      {/* Add Medicine Modal */}
      {showAddMedicineModal && (
        <AddMedicineModal
          onClose={() => setShowAddMedicineModal(false)}
          onAddMedicine={addMedicineToInvoice}
          BASE_URL={BASE_URL}
        />
      )}
    </div>
  );
}

export default App;
