import React, { useEffect, useState, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import { Eye, Plus, X, FileDown } from "lucide-react";
import axios from "axios";
import BoxLoader from "../../looader/BoxLoader";

const StaffDirectory = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [sortBy, setSortBy] = useState("name");
  const [error, setError] = useState(null);
  const [isExporting, setIsExporting] = useState(false);
  const navigate = useNavigate();

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`${import.meta.env.VITE_API_URL}/admin/employees`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEmployees(response.data);
    } catch (err) {
      setError("Failed to fetch employees");
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async () => {
    try {
      setIsExporting(true);
      const token = localStorage.getItem("token");
      const response = await axios.get(`${import.meta.env.VITE_API_URL}/admin/employees/export`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
        responseType: "blob",
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "employees.xlsx");
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      console.error("Export failed", err);
    } finally {
      setIsExporting(false);
    }
  };

  const handleCardClick = async (employee) => {
    try {
      setIsLoading(true);
      setSelectedEmployee(employee);
      setIsModalOpen(true);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const filteredEmployees = useMemo(() => {
    return employees.filter((employee) =>
      employee.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [employees, searchQuery]);

  const sortedEmployees = useMemo(() => {
    return filteredEmployees.sort((a, b) => {
      const aValue = a[sortBy]?.toString().toLowerCase() || "";
      const bValue = b[sortBy]?.toString().toLowerCase() || "";
      return sortOrder === "asc"
        ? aValue.localeCompare(bValue)
        : bValue.localeCompare(aValue);
    });
  }, [filteredEmployees, sortOrder, sortBy]);

  return (
    <div className="min-h-full bg-theme-70 p-4 sm:p-6 font-sans relative">
      {/* Global loader */}
      {(loading || isExporting || isLoading) && (
        <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-white/30">
          <BoxLoader />
        </div>
      )}

      {/* Header */}
      <div className="bg-theme-200 rounded-xl p-6 sm:p-8 mb-6 sm:mb-8">
        <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-2 sm:mb-3">Staff Directory</h1>
        <p className="text-gray-600 text-base sm:text-lg">
          View and manage your pharmacy staff members
        </p>
      </div>

      {/* Top Buttons */}
      <div className="flex flex-col sm:flex-row justify-end items-center gap-4 mb-4">
        <button
          onClick={handleExport}
          className="flex items-center gap-2 bg-orange-500 hover:bg-orange-600 text-white font-semibold px-4 sm:px-5 py-2 rounded-lg shadow-lg transition-colors"
          disabled={isExporting}
        >
          <FileDown className="w-4 sm:w-5 h-4 sm:h-5" />
          Excel Report
        </button>
        <button
          onClick={() => navigate("/home/staff/add")}
          className="flex items-center gap-2 bg-theme-500 hover:bg-theme-600 text-white font-semibold px-4 sm:px-5 py-2 rounded-lg shadow-lg transition-colors"
        >
          <Plus className="w-4 sm:w-5 h-4 sm:h-5" />
          Add Employee
        </button>
      </div>

      {/* Search & Sort Controls */}
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4 mb-6">
        <input
          type="text"
          placeholder="Search by name"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="px-4 py-2 w-full sm:max-w-sm rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-theme-500 transition"
        />

        <div className="flex gap-4 items-center">
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="px-4 py-2 rounded-lg border border-gray-300"
          >
            <option value="name">Sort by Name</option>
            <option value="email">Sort by Email</option>
            <option value="role">Sort by Role</option>
          </select>

          <button
            onClick={() =>
              setSortOrder((prev) => (prev === "asc" ? "desc" : "asc"))
            }
            className="px-4 py-2 rounded-lg bg-theme-500 text-white font-medium hover:bg-theme-600 transition"
          >
            {sortOrder === "asc" ? "Asc" : "Desc"}
          </button>
        </div>
      </div>

      {/* Staff Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 sm:gap-6">
        {sortedEmployees.map((employee) => (
          <div
            key={employee.employee_id}
            className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition cursor-pointer"
            onClick={() => handleCardClick(employee)}
          >
            <h3 className="text-lg font-semibold text-gray-900">{employee.name}</h3>
            <p className="text-sm text-gray-500">{employee.email}</p>
            <p className="text-sm text-gray-600">{employee.role}</p>
          </div>
        ))}
      </div>

      {/* Employee Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 p-4">
          <div className="bg-white rounded-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto relative">
            <div className="flex justify-between items-center p-4 border-b">
              <h2 className="text-xl font-semibold text-gray-800">Employee Details</h2>
              <button onClick={() => setIsModalOpen(false)}>
                <X className="w-6 h-6 text-gray-500 hover:text-gray-700" />
              </button>
            </div>

            <div className="p-6">
              {isLoading ? (
                <div className="flex justify-center items-center py-12">
                  <BoxLoader />
                </div>
              ) : selectedEmployee ? (
                <>
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <div>
                      <strong>Name:</strong> {selectedEmployee.name}
                    </div>
                    <div>
                      <strong>Email:</strong> {selectedEmployee.email}
                    </div>
                    <div>
                      <strong>Role:</strong> {selectedEmployee.role}
                    </div>
                    <div>
                      <strong>Gender:</strong> {selectedEmployee.gender}
                    </div>
                    <div>
                      <strong>Phone:</strong> {selectedEmployee.phone}
                    </div>
                    <div>
                      <strong>Address:</strong> {selectedEmployee.address}
                    </div>
                    <div>
                      <strong>Created At:</strong>{" "}
                      {new Date(selectedEmployee.created_at).toLocaleString()}
                    </div>
                  </div>
                </>
              ) : (
                <p className="text-gray-500 text-center">No employee data found.</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StaffDirectory;
