import React, { useState, useRef, useEffect, useCallback } from "react";

export default function AddMedicineModal({ onClose, onAdd, BASE_URL }) {
  const [fields, setFields] = useState({
    medicine_name: "",
    brand_name: "",
    mfg_date: "",
    expiry_date: "",
    packed_type: "",
    stock_quantity: "",
    purchase_price: "",
    mrp: "",
    batch_no: "",
  });
  const [suggestions, setSuggestions] = useState([]);
  const [highlighted, setHighlighted] = useState(-1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const debounceRef = useRef();

  // Debounced fetch for medicine name suggestions
  useEffect(() => {
    if (!fields.medicine_name) {
      setSuggestions([]);
      return;
    }
    if (debounceRef.current) clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(async () => {
      try {
        const res = await fetch(
          `${BASE_URL}/admin/medicines/recommendation?query=${fields.medicine_name}`
        );
        const data = await res.json();
        if (data.status === "success") setSuggestions(data.recommendations);
        else setSuggestions([]);
      } catch {
        setSuggestions([]);
      }
    }, 400);
    // eslint-disable-next-line
  }, [fields.medicine_name]);

  // Keyboard navigation for suggestions
  const handleKeyDown = (e) => {
    if (suggestions.length === 0) return;
    if (e.key === "ArrowDown") {
      setHighlighted((h) => (h + 1) % suggestions.length);
    } else if (e.key === "ArrowUp") {
      setHighlighted((h) => (h - 1 + suggestions.length) % suggestions.length);
    } else if (e.key === "Enter" && highlighted >= 0) {
      selectSuggestion(suggestions[highlighted]);
    }
  };

  // Select a suggestion and fetch its details
  const selectSuggestion = async (suggestion) => {
    setFields((f) => ({ ...f, medicine_name: suggestion.medicine_name }));
    setSuggestions([]);
    setHighlighted(-1);
    setLoading(true);
    try {
      const res = await fetch(`${BASE_URL}/admin/medicine/${suggestion.medicine_id}`);
      const data = await res.json();
      if (data.status === "success" && data.medicine[0]) {
        const m = data.medicine[0];
        setFields({
          medicine_name: m.medicine_name,
          brand_name: m.brand_name,
          mfg_date: m.mfg_date.slice(0, 10),
          expiry_date: m.expiry_date.slice(0, 10),
          packed_type: m.packed_type,
          stock_quantity: m.stock_quantity,
          purchase_price: m.purchase_price,
          mrp: m.mrp,
          batch_no: m.batch_no,
        });
      }
    } catch {}
    setLoading(false);
  };

  // Handle input changes
  const handleChange = (e) => {
    setFields({ ...fields, [e.target.name]: e.target.value });
    if (e.target.name === "medicine_name") setHighlighted(-1);
  };

  // Add medicine to parent
  const handleAdd = () => {
    // Basic validation
    if (
      !fields.medicine_name ||
      !fields.brand_name ||
      !fields.mfg_date ||
      !fields.expiry_date ||
      !fields.packed_type ||
      !fields.stock_quantity ||
      !fields.purchase_price ||
      !fields.mrp ||
      !fields.batch_no
    ) {
      setError("All fields are required.");
      return;
    }
    setError("");
    onAdd(fields);
    setFields({
      medicine_name: "",
      brand_name: "",
      mfg_date: "",
      expiry_date: "",
      packed_type: "",
      stock_quantity: "",
      purchase_price: "",
      mrp: "",
      batch_no: "",
    });
  };

  // Refresh/clear all fields
  const handleRefresh = () => {
    setFields({
      medicine_name: "",
      brand_name: "",
      mfg_date: "",
      expiry_date: "",
      packed_type: "",
      stock_quantity: "",
      purchase_price: "",
      mrp: "",
      batch_no: "",
    });
    setSuggestions([]);
    setHighlighted(-1);
    setError("");
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-lg w-full max-w-lg p-6 relative">
        {/* Header */}
        <div className="flex justify-between items-center mb-2">
          <h2 className="text-xl font-bold">Add New Medicine</h2>
          <button
            className="text-gray-500 hover:bg-gray-200 rounded-full p-2"
            onClick={onClose}
            aria-label="Close"
          >
            ×
          </button>
        </div>
        <p className="text-gray-600 mb-4">Enter details for the medicine to add to stock.</p>

        {/* Form */}
        <div className="space-y-3">
          <div className="relative">
            <label className="block text-sm font-medium mb-1">Medicine Name</label>
            <input
              name="medicine_name"
              value={fields.medicine_name}
              onChange={handleChange}
              onKeyDown={handleKeyDown}
              className="w-full border rounded px-3 py-2"
              autoComplete="off"
            />
            {/* Suggestions */}
            {suggestions.length > 0 && (
              <ul className="absolute z-10 bg-white border rounded w-full mt-1 shadow">
                {suggestions.map((s, i) => (
                  <li
                    key={s.medicine_id}
                    className={`px-3 py-2 cursor-pointer ${
                      i === highlighted ? "bg-blue-100" : ""
                    }`}
                    onMouseDown={() => selectSuggestion(s)}
                  >
                    {s.medicine_name}
                  </li>
                ))}
              </ul>
            )}
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Brand</label>
            <input
              name="brand_name"
              value={fields.brand_name}
              onChange={handleChange}
              className="w-full border rounded px-3 py-2"
            />
          </div>
          <div className="grid grid-cols-2 gap-2">
            <div>
              <label className="block text-sm font-medium mb-1">MFG Date</label>
              <input
                type="date"
                name="mfg_date"
                value={fields.mfg_date}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Expiry Date</label>
              <input
                type="date"
                name="expiry_date"
                value={fields.expiry_date}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2"
              />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Packed Type</label>
            <input
              name="packed_type"
              value={fields.packed_type}
              onChange={handleChange}
              className="w-full border rounded px-3 py-2"
            />
          </div>
          <div className="grid grid-cols-2 gap-2">
            <div>
              <label className="block text-sm font-medium mb-1">Quantity</label>
              <input
                type="number"
                name="stock_quantity"
                value={fields.stock_quantity}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Batch No</label>
              <input
                name="batch_no"
                value={fields.batch_no}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2"
              />
            </div>
          </div>
          <div className="grid grid-cols-2 gap-2">
            <div>
              <label className="block text-sm font-medium mb-1">Purchase Price (₹)</label>
              <input
                type="number"
                name="purchase_price"
                value={fields.purchase_price}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">MRP (₹)</label>
              <input
                type="number"
                name="mrp"
                value={fields.mrp}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2"
              />
            </div>
          </div>
        </div>
        {error && <div className="text-red-600 mt-2">{error}</div>}

        {/* Modal Actions */}
        <div className="flex justify-end gap-2 mt-6">
          <button
            className="px-4 py-2 rounded bg-gray-200 hover:bg-gray-300"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="px-4 py-2 rounded bg-yellow-500 text-white hover:bg-yellow-600"
            onClick={handleRefresh}
            type="button"
          >
            Refresh
          </button>
          <button
            className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700"
            onClick={handleAdd}
            disabled={loading}
          >
            {loading ? "Adding..." : "Add"}
          </button>
        </div>
      </div>
    </div>
  );
}
