import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { 
  Menu, 
  X, 
  Home, 
  Package, 
  Plus, 
  BarChart3, 
  DollarSign, 
  CheckSquare, 
  Users, 
  Settings,
  ChevronRight,
  Building
} from 'lucide-react';

const Layout = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [activeMenu, setActiveMenu] = useState('Dashboard');
  const navigate = useNavigate();
  const location = useLocation();

  // Menu items configuration
  const menuItems = [
    { name: 'Dashboard', icon: Home, path: '/dashboard' },
    { name: 'Add Stock', icon: Plus, path: '/add-stock' },
    { name: 'Stock', icon: Package, path: '/stock' },
    { name: 'Report', icon: BarChart3, path: '/report' },
    { name: 'FinTrack', icon: DollarSign, path: '/fintrack' },
    { name: 'Todo', icon: CheckSquare, path: '/todo' },
    { name: 'Staff', icon: Users, path: '/staff' },
    { name: 'Setting', icon: Settings, path: '/setting' }
  ];

  // Generate breadcrumb based on active menu
  const getBreadcrumb = () => {
    const currentItem = menuItems.find(item => item.name === activeMenu);
    if (currentItem) {
      return ['Dashboard', currentItem.name];
    }
    return ['Dashboard'];
  };

  const handleMenuClick = (menuName, path) => {
    setActiveMenu(menuName);
    setSidebarOpen(false); // Close sidebar on mobile
    navigate(path);
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Sidebar */}
      <div className={`
        fixed inset-y-0 left-0 z-50 w-64 bg-sky-100 transform transition-transform duration-300 ease-in-out
        ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} lg:translate-x-0 lg:static lg:inset-0
      `}>
        {/* Sidebar Header */}
        <div className="flex items-center justify-between p-6 border-b border-sky-200">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-sky-500 rounded-lg flex items-center justify-center">
              <Building className="w-5 h-5 text-white" />
            </div>
            <h1 className="text-xl font-bold text-gray-900">pharmaDesk</h1>
          </div>
          <button
            onClick={() => setSidebarOpen(false)}
            className="lg:hidden p-1 rounded-md text-gray-500 hover:text-gray-700"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Menu Items */}
        <nav className="mt-6 px-4">
          <ul className="space-y-2">
            {menuItems.map((item) => {
              const Icon = item.icon;
              const isActive = activeMenu === item.name;
              
              return (
                <li key={item.name}>
                  <button
                    onClick={() => handleMenuClick(item.name, item.path)}
                    className={`
                      w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-left transition-colors duration-200
                      ${isActive 
                        ? 'bg-sky-500 text-white shadow-sm' 
                        : 'text-gray-700 hover:bg-sky-200 hover:text-gray-900'
                      }
                    `}
                  >
                    <Icon className="w-5 h-5" />
                    <span className="font-medium">{item.name}</span>
                  </button>
                </li>
              );
            })}
          </ul>
        </nav>
      </div>

      {/* Main Content Area */}
      <div className="lg:pl-64">
        {/* Top Bar / Header */}
        <header className="bg-gray-100 border-b border-gray-200 sticky top-0 z-30">
          <div className="flex items-center justify-between p-4 px-6">
            {/* Left side - Hamburger and Breadcrumb */}
            <div className="flex items-center space-x-4">
              {/* Hamburger Menu Button */}
              <button
                onClick={toggleSidebar}
                className="lg:hidden p-2 rounded-md text-gray-500 hover:text-gray-700 hover:bg-gray-200"
              >
                <Menu className="w-5 h-5" />
              </button>

              {/* Breadcrumb */}
              <nav className="flex items-center space-x-2">
                {getBreadcrumb().map((item, index) => (
                  <div key={index} className="flex items-center">
                    {index > 0 && (
                      <ChevronRight className="w-4 h-4 text-gray-400 mx-2" />
                    )}
                    <span className={`
                      text-sm ${index === getBreadcrumb().length - 1 
                        ? 'text-gray-900 font-semibold' 
                        : 'text-gray-600'
                      }
                    `}>
                      {item}
                    </span>
                  </div>
                ))}
              </nav>
            </div>

            {/* Right side - User info or actions */}
            <div className="flex items-center space-x-4">
              <div className="hidden sm:flex items-center space-x-2 text-sm text-gray-600">
                <span>Welcome back,</span>
                <span className="font-medium text-gray-900">Admin</span>
              </div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="p-4">
          {children}
        </main>
      </div>
    </div>
  );
};

export default Layout;
