import React, { useState } from "react";
import AddMedicineModal from "./AddMedicineModal";

const  BASE_URL  = "http://localhost:4000";


function StockEntry() {
  const [wholesaler, setWholesaler] = useState("");
  const [invoiceNumber, setInvoiceNumber] = useState("");
  const [date, setDate] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleAddMedicine = (medicine) => {
    setMedicines((prev) => [...prev, medicine]);
  };

  const handleDeleteMedicine = (idx) => {
    setMedicines((prev) => prev.filter((_, i) => i !== idx));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setMessage(null);

    if (!wholesaler || !invoiceNumber || !date) {
      setError("Please fill all required fields.");
      return;
    }
    if (medicines.length === 0) {
      setError("Please add at least one medicine.");
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(`${BASE_URL}/admin/medicine`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          wholesaler,
          invoiceNumber,
          medicine: medicines,
        }),
      });
      const data = await res.json();
      if (res.ok) {
        setMessage(data.message || "Stock entry added!");
        setWholesaler("");
        setInvoiceNumber("");
        setDate("");
        setMedicines([]);
      } else {
        setError(data.message || "Failed to add stock entry.");
      }
    } catch (err) {
      setError("Network error.");
    }
    setLoading(false);
    setTimeout(() => {
      setMessage(null);
      setError(null);
    }, 3000);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <header className="flex items-center px-4 py-4 bg-white shadow">
        <span className="text-2xl mr-2 cursor-pointer" onClick={() => window.history.back()}>←</span>
        <h1 className="text-xl font-bold">Add New Stock Entry</h1>
      </header>
      <form className="max-w-3xl mx-auto bg-white rounded-lg shadow p-6 mt-8" onSubmit={handleSubmit}>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div>
            <label className="block font-medium mb-1">Wholesaler <span className="text-red-500">*</span></label>
            <input className="w-full border rounded px-3 py-2 focus:outline-none focus:ring" value={wholesaler} onChange={(e) => setWholesaler(e.target.value)} required />
          </div>
          <div>
            <label className="block font-medium mb-1">Invoice Number <span className="text-red-500">*</span></label>
            <input className="w-full border rounded px-3 py-2 focus:outline-none focus:ring" value={invoiceNumber} onChange={(e) => setInvoiceNumber(e.target.value)} required />
          </div>
          <div>
            <label className="block font-medium mb-1">Date <span className="text-red-500">*</span></label>
            <input type="date" className="w-full border rounded px-3 py-2 focus:outline-none focus:ring" value={date} onChange={(e) => setDate(e.target.value)} required />
          </div>
        </div>
        <div className="mb-4">
          <div className="flex justify-between items-center mb-2">
            <h2 className="font-semibold text-lg">Medicines in this Invoice</h2>
            <button type="button" className="flex items-center bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700" onClick={() => setShowModal(true)}>
              <span className="text-xl mr-1">+</span> Add Medicine
            </button>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border rounded">
              <thead>
                <tr className="bg-gray-100">
                  <th className="px-2 py-2">Medicine Name</th>
                  <th className="px-2 py-2">Brand</th>
                  <th className="px-2 py-2">MFG Date</th>
                  <th className="px-2 py-2">Expiry Date</th>
                  <th className="px-2 py-2">Packed Type</th>
                  <th className="px-2 py-2">Quantity</th>
                  <th className="px-2 py-2">Purchase Price</th>
                  <th className="px-2 py-2">MRP</th>
                  <th className="px-2 py-2">Batch No</th>
                  <th className="px-2 py-2">Action</th>
                </tr>
              </thead>
              <tbody>
                {medicines.length === 0 ? (
                  <tr>
                    <td colSpan={10} className="text-center py-4 text-gray-400">No medicines added yet.</td>
                  </tr>
                ) : (
                  medicines.map((med, idx) => (
                    <tr key={idx} className={idx % 2 === 0 ? "bg-white" : "bg-gray-50"}>
                      <td className="px-2 py-2">{med.medicine_name}</td>
                      <td className="px-2 py-2">{med.brand_name}</td>
                      <td className="px-2 py-2">{med.mfg_date}</td>
                      <td className="px-2 py-2">{med.expiry_date}</td>
                      <td className="px-2 py-2">{med.packed_type}</td>
                      <td className="px-2 py-2">{med.stock_quantity}</td>
                      <td className="px-2 py-2">{med.purchase_price}</td>
                      <td className="px-2 py-2">{med.mrp}</td>
                      <td className="px-2 py-2">{med.batch_no}</td>
                      <td className="px-2 py-2">
                        <button type="button" className="text-red-500 hover:text-red-700" onClick={() => handleDeleteMedicine(idx)} title="Delete">🗑️</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
        {error && <div className="mb-2 text-red-600 font-medium">{error}</div>}
        {message && <div className="mb-2 text-green-600 font-medium">{message}</div>}
        <div className="flex justify-end space-x-2 mt-6">
          <button type="button" className="px-4 py-2 rounded bg-gray-200 hover:bg-gray-300" onClick={() => { setWholesaler(""); setInvoiceNumber(""); setDate(""); setMedicines([]); setError(null); setMessage(null); }} disabled={loading}>Cancel</button>
          <button type="submit" className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700" disabled={loading}>{loading ? "Submitting..." : "Submit Stock Entry"}</button>
        </div>
      </form>
      {showModal && (
        <AddMedicineModal onClose={() => setShowModal(false)} onAddMedicine={handleAddMedicine} />
      )}
    </div>
  );
}

export default StockEntry;
