import React from 'react';
import { BarChart, FileText, Calendar, LineChart, ChevronRight } from 'lucide-react';

const reportCards = [
  {
    id: 1,
    icon: BarChart,
    title: "Supplier Insights",
    description: "Track purchases by wholesaler",
    color: "text-sky-600",
    hoverColor: "hover:bg-sky-50"
  },
  {
    id: 2,
    icon: FileText,
    title: "Transaction Details",
    description: "View and manage invoices",
    color: "text-purple-600",
    hoverColor: "hover:bg-purple-50"
  },
  {
    id: 3,
    icon: Calendar,
    title: "Expiring Stock",
    description: "Monitor upcoming expirations",
    color: "text-red-600",
    hoverColor: "hover:bg-red-50"
  },
  {
    id: 4,
    icon: LineChart,
    title: "Revenue & Trends",
    description: "Analyze sales performance",
    color: "text-green-600",
    hoverColor: "hover:bg-green-50"
  }
];

const ReportsDashboard = () => {
  return (
    <div className="w-full min-h-full bg-gray-50 p-6">
      {/* Breadcrumb */}
      <div className="flex items-center space-x-2 text-sm text-gray-600 mb-8">
        <span className="font-medium text-sky-700">PharmaDesk</span>
        <ChevronRight className="w-4 h-4 text-gray-400" />
        <span className="font-semibold text-gray-900">Reports</span>
      </div>

      {/* Overview Section */}
      <div className="bg-sky-50 rounded-xl p-8 mb-8">
        <h1 className="text-2xl font-bold text-sky-900 mb-2">Reports Overview</h1>
        <p className="text-gray-600">
          Access various reports to gain insights into your pharmacy operations.
        </p>
      </div>

      {/* Reports Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {reportCards.map((card) => {
          const Icon = card.icon;
          return (
            <button
              key={card.id}
              className={`bg-white rounded-xl p-6 shadow-sm hover:shadow-md transition-all duration-200 
                transform hover:scale-[1.02] ${card.hoverColor} flex flex-col items-center text-center`}
            >
              <Icon className={`w-12 h-12 ${card.color} mb-4`} />
              <h3 className={`font-semibold text-lg mb-2 ${card.color}`}>
                {card.title}
              </h3>
              <p className="text-gray-600 text-sm">{card.description}</p>
            </button>
          )
        })}
      </div>
    </div>
  );
};

export default ReportsDashboard;
