import React, { useState } from "react";
import AddMedicineModal from "./AddMedicineModal";

const BASE_URL = "http://your-api-base-url";

export default function App() {
  const [wholesaler, setWholesaler] = useState("");
  const [invoiceNumber, setInvoiceNumber] = useState("");
  const [date, setDate] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [toast, setToast] = useState(null);

  const handleAddMedicine = (medicine) => {
    setMedicines((prev) => [...prev, medicine]);
    setShowModal(false);
  };

  const handleDeleteMedicine = (idx) => {
    setMedicines((prev) => prev.filter((_, i) => i !== idx));
  };

  const handleSubmit = async () => {
    if (!wholesaler || !invoiceNumber || !date || medicines.length === 0) {
      setToast({ type: "error", message: "All fields and at least one medicine are required." });
      return;
    }
    setLoading(true);
    try {
      const res = await fetch(`${BASE_URL}/admin/medicine`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          wholesaler,
          invoiceNumber,
          medicine: medicines.map((m) => ({
            packed_type: m.packed_type,
            medicine_name: m.medicine_name,
            brand_name: m.brand_name,
            batch_no: m.batch_no,
            purchase_price: m.purchase_price,
            mrp: m.mrp,
            stock_quantity: m.stock_quantity,
            mfg_date: m.mfg_date,
            expiry_date: m.expiry_date,
          })),
        }),
      });
      const data = await res.json();
      if (res.ok) {
        setToast({ type: "success", message: data.message });
        setWholesaler("");
        setInvoiceNumber("");
        setDate("");
        setMedicines([]);
      } else {
        setToast({ type: "error", message: data.message || "Error submitting stock entry." });
      }
    } catch (e) {
      setToast({ type: "error", message: "Network error." });
    }
    setLoading(false);
    setTimeout(() => setToast(null), 3000);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      {/* Header */}
      <div className="flex items-center mb-6">
        <button
          className="mr-3 text-2xl"
          onClick={() => window.history.back()}
          aria-label="Back"
        >
          ‚Üê
        </button>
        <h1 className="text-2xl font-bold">Add New Stock Entry</h1>
      </div>

      {/* Main Form */}
      <div className="bg-white rounded-lg shadow p-6 mb-8 max-w-3xl mx-auto">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium mb-1">Wholesaler</label>
            <input
              className="w-full border rounded px-3 py-2"
              value={wholesaler}
              onChange={(e) => setWholesaler(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Invoice Number</label>
            <input
              className="w-full border rounded px-3 py-2"
              value={invoiceNumber}
              onChange={(e) => setInvoiceNumber(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Date</label>
            <input
              type="date"
              className="w-full border rounded px-3 py-2"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>
        </div>

        {/* Medicines Table */}
        <div>
          <div className="flex justify-between items-center mb-2">
            <h2 className="font-semibold text-lg">Medicines in this Invoice</h2>
            <button
              className="flex items-center bg-blue-600 text-white px-4 py-2 rounded shadow hover:bg-blue-700"
              onClick={() => setShowModal(true)}
            >
              <span className="mr-2 text-xl">+</span> Add Medicine
            </button>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border rounded">
              <thead>
                <tr className="bg-gray-50">
                  <th className="px-2 py-2 border">Medicine Name</th>
                  <th className="px-2 py-2 border">Brand</th>
                  <th className="px-2 py-2 border">MFG Date</th>
                  <th className="px-2 py-2 border">Expiry Date</th>
                  <th className="px-2 py-2 border">Packed Type</th>
                  <th className="px-2 py-2 border">Quantity</th>
                  <th className="px-2 py-2 border">Purchase Price</th>
                  <th className="px-2 py-2 border">MRP</th>
                  <th className="px-2 py-2 border">Batch No</th>
                  <th className="px-2 py-2 border">Action</th>
                </tr>
              </thead>
              <tbody>
                {medicines.length === 0 ? (
                  <tr>
                    <td colSpan={10} className="text-center py-4 text-gray-400">
                      No medicines added yet.
                    </td>
                  </tr>
                ) : (
                  medicines.map((m, idx) => (
                    <tr key={idx} className={idx % 2 === 0 ? "bg-white" : "bg-gray-50"}>
                      <td className="border px-2 py-1">{m.medicine_name}</td>
                      <td className="border px-2 py-1">{m.brand_name}</td>
                      <td className="border px-2 py-1">{m.mfg_date}</td>
                      <td className="border px-2 py-1">{m.expiry_date}</td>
                      <td className="border px-2 py-1">{m.packed_type}</td>
                      <td className="border px-2 py-1">{m.stock_quantity}</td>
                      <td className="border px-2 py-1">‚Çπ{m.purchase_price}</td>
                      <td className="border px-2 py-1">‚Çπ{m.mrp}</td>
                      <td className="border px-2 py-1">{m.batch_no}</td>
                      <td className="border px-2 py-1 text-center">
                        <button
                          className="text-red-500 hover:text-red-700"
                          onClick={() => handleDeleteMedicine(idx)}
                          aria-label="Delete"
                        >
                          üóëÔ∏è
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end gap-3 mt-6">
          <button
            className="px-5 py-2 rounded bg-gray-200 hover:bg-gray-300"
            onClick={() => {
              setWholesaler("");
              setInvoiceNumber("");
              setDate("");
              setMedicines([]);
            }}
            disabled={loading}
          >
            Cancel
          </button>
          <button
            className="px-5 py-2 rounded bg-blue-600 text-white hover:bg-blue-700"
            onClick={handleSubmit}
            disabled={loading}
          >
            {loading ? "Submitting..." : "Submit Stock Entry"}
          </button>
        </div>
      </div>

      {/* Toast Notification */}
      {toast && (
        <div
          className={`fixed bottom-6 right-6 px-6 py-3 rounded shadow-lg text-white font-semibold z-50 ${
            toast.type === "success" ? "bg-green-600" : "bg-red-600"
          }`}
        >
          {toast.message}
        </div>
      )}

      {/* Add Medicine Modal */}
      {showModal && (
        <AddMedicineModal
          onClose={() => setShowModal(false)}
          onAdd={handleAddMedicine}
          BASE_URL={BASE_URL}
        />
      )}
    </div>
  );
}
