  "#8E4585",
  "#FADADD",
  "#8B008B",
  "#87CEEB",
  "#4B0082",
  "#FFDAB9",
  "#E2725B",
  "#B0E0E6",
  "#264653",
  "#FFFAFA",
  "#000000",
  "#F8F8FF",
  "#B0C4DE",
  "#003153",
  "#F08080",
  "#5C4033",
  "#FFF8DC",
  "#006400",
  "#E5E4E2",
